-- 126. Brandy Alexander
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c3d4e5f6-a7b8-4c9d-0e1f-2a3b4c5d6e7f', 'brandy-alexander', 'brandy_alexander_cognac_cacao_cream', '{"en":"Brandy Alexander"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","classic","chocolate","brandy-based"}', '{"en":"The most popular variation of the Alexander, made with brandy (or cognac) instead of gin. This elegant, creamy cocktail combines the warmth of brandy with the richness of chocolate and cream, creating a sophisticated after-dinner indulgence."}'::jsonb, '{"created_year":"1920s","origin":{"city":"London","country":"England"},"story":{"en":"The Brandy Alexander emerged in the 1920s-30s as a more refined variation of the original gin-based Alexander. While the exact origin is debated, it became the definitive version of the drink, especially popular during the mid-20th century. The cocktail enjoyed particular fame after being featured in the film \"Days of Wine and Roses\" (1962) and was reportedly a favorite of John Lennon. Its popularity peaked in the 1970s as a glamorous after-dinner drink."},"named_after":{"en":"Named after the original Alexander cocktail, with \"Brandy\" added to distinguish it from the gin-based version."}}'::jsonb, '{"profile":["creamy","sweet","chocolate","boozy"],"description":{"en":"Luxuriously smooth and velvety with a perfect marriage of chocolate, cream, and warming brandy. More complex than its gin-based predecessor, with deeper, richer flavors and a sophisticated finish."},"first_impression":{"en":"Silky chocolate cream followed immediately by warm brandy notes"},"finish":{"en":"Long, warming finish with nutmeg spice and lingering cocoa"},"balance":{"en":"Beautifully balanced between sweetness, cream, and spirit - more sophisticated than most dessert cocktails"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","date_night","celebration","nightcap"],"seasons":["autumn","winter","all_seasons"],"food_pairings":{"en":"Excellent with chocolate desserts, crème brûlée, tiramisu, or as a dessert replacement. Pairs beautifully with aged cheese, chocolate truffles, and espresso."},"ideal_for":{"en":"Perfect for brandy and cognac lovers who appreciate creamy, indulgent cocktails. Ideal for those seeking a sophisticated dessert drink with historical pedigree. A classic choice for elegant dinners and special occasions."}}'::jsonb, '[{"ingredient_id":"ING_COGNAC","quantity":{"amount":40,"unit":"ml"},"display_name":"Cognac (or Brandy)","notes":null},{"ingredient_id":"ING_CREME_DE_CACAO_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Crème de Cacao (dark)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 10-15 seconds until well-chilled and frothy. Double strain into a chilled coupe or martini glass. Grate fresh nutmeg over the top."}'::jsonb, 'Coupe (or Martini)', '{"en":"Freshly grated nutmeg"}'::jsonb, 'none', 'up', '{"ING_COGNAC"}', '{"creamy","sweet","chocolate","boozy"}', 20, 290, 'easy', 60, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter","all-seasons"}', '{"digestivo","date-night","celebration","nightcap"}', false, false, '{"alexander","coffee-alexander","alexander-sister"}', 'This is the more popular version of the Alexander. Always use freshly grated nutmeg. Shake hard to properly emulsify cream. Can use quality brandy instead of cognac for cost savings. Some recipes call for equal parts (30ml each) - adjust to guest preference.', 'mid', 70, 'https://www.diffordsguide.com/cocktails/recipe/253/brandy-alexander', 'Classic variation of the Alexander, more popular than the original gin-based version.', 1)
