-- 128. Brown Derby
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e1f2a3b4-c5d6-4e7f-8a9b-0c1d2e3f4a5b', 'brown-derby', '5a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a11', '{"en":"Brown Derby"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","bourbon","citrus","refreshing"}', '{"en":"A simple yet sophisticated bourbon cocktail featuring grapefruit juice and honey syrup. The Brown Derby is a refreshing, citrus-forward drink with a touch of sweetness that became a Hollywood favorite."}'::jsonb, '{"created_year":"1930","origin":{"city":"Los Angeles","bar":"Brown Derby Restaurant","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"Created at the legendary Brown Derby Restaurant in Los Angeles during the 1930s, this cocktail became a favorite among Hollywood celebrities. The Brown Derby was one of the most famous restaurants of the Golden Age of Hollywood, frequented by stars like Clark Gable and Greta Garbo. The cocktail''s simple recipe of bourbon, grapefruit, and honey created a refreshing drink that matched the California lifestyle. After falling into obscurity, it was rediscovered during the modern cocktail renaissance."},"named_after":{"en":"Named after the Brown Derby Restaurant, a Hollywood landmark famous for its distinctive hat-shaped building."}}'::jsonb, '{"profile":["citrus","sweet","refreshing"],"description":{"en":"Bright and refreshing with balanced sweetness. Bourbon provides a warm, oaky backbone, fresh grapefruit juice adds tart citrus notes, and honey syrup brings natural sweetness and smooth texture. The combination is deceptively simple but perfectly balanced."},"first_impression":{"en":"Tart grapefruit and honey sweetness hit first, followed by warming bourbon"},"finish":{"en":"Clean, refreshing finish with lingering citrus and subtle honey notes"},"balance":{"en":"Perfectly balanced between tart grapefruit and sweet honey, with bourbon providing depth"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["brunch","poolside","summer_party","casual_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with brunch dishes, grilled chicken, citrus salads, and light seafood. The bright citrus complements fresh, summery flavors."},"ideal_for":{"en":"Perfect for bourbon lovers seeking a lighter, more refreshing serve. Ideal for those who enjoy citrus-forward cocktails with natural sweetness."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":45,"unit":"ml"},"display_name":"Bourbon","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Honey syrup (1:1)","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe glass. Garnish with a grapefruit twist."}'::jsonb, 'Coupe', '{"en":"Grapefruit twist"}'::jsonb, 'none', 'up', '{"ING_BOURBON"}', '{"citrus","sweet","refreshing"}', 18, 160, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","poolside","summer_party","casual_gathering"}', false, false, '{"whiskey-sour","gold-rush"}', 'Use fresh grapefruit juice for best results. Honey syrup should be 1:1 ratio (honey:hot water). Shake hard to properly emulsify the honey. Can serve on rocks for a longer drink.', 'mid', 76, 'Brown Derby Restaurant, Los Angeles, 1930s', 'Classic Hollywood cocktail. Rediscovered during modern cocktail renaissance.', 1)
