-- 129. Buttery Nipple
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d0e1f2a3-b4c5-4d6e-7f8a-9b0c1d2e3f4a', 'buttery-nipple', 'buttery_nipple_butterscotch_baileys_shot', '{"en":"Buttery Nipple"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","shooter","sweet","layered"}', '{"en":"A popular layered shooter combining butterscotch schnapps and Irish cream. This sweet, creamy shot has a smooth buttery flavor that makes it a favorite at bars and parties, despite its provocative name."}'::jsonb, '{"created_year":"1980s","origin":{"country":"USA"},"story":{"en":"The Buttery Nipple emerged in the 1980s during the shooter craze that swept American bars. It became one of the most popular layered shots of the era, known for its sweet, approachable flavor and eye-catching presentation. The drink''s popularity was driven by the rise of schnapps and cream liqueurs in American drinking culture. Its suggestive name made it a frequent request at parties and bachelor/bachelorette celebrations. The shot remains popular today as a gateway drink for those new to spirits."},"named_after":{"en":"The suggestive name refers to the creamy, buttery appearance and smooth texture of the layered shot."}}'::jsonb, '{"profile":["creamy","sweet","buttery","caramel"],"description":{"en":"Extremely sweet with pronounced butterscotch and caramel flavors balanced by creamy Irish cream. The combination creates a smooth, buttery shot that goes down easily with minimal alcohol burn."},"first_impression":{"en":"Sweet butterscotch cream with immediate caramel notes"},"finish":{"en":"Smooth, sweet finish with lingering butterscotch"},"balance":{"en":"Very sweet and dessert-focused - designed for easy drinking rather than complexity"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","casual","bachelor_party"],"seasons":["all_seasons"],"food_pairings":{"en":"Typically consumed as a standalone shot between other drinks. Can accompany desserts or be served as a liquid dessert itself."},"ideal_for":{"en":"Perfect for those new to shots or who prefer very sweet drinks. Ideal for parties, celebrations, and social drinking occasions. Popular with guests who want an easy, non-threatening shot experience."}}'::jsonb, '[{"ingredient_id":"ING_BUTTERSCOTCH_SCHNAPPS","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Butterscotch schnapps","notes":null},{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null}]'::jsonb, 'layer', '{"en":"Pour butterscotch schnapps into a shot glass. Using the back of a bar spoon, slowly layer the Irish cream on top by pouring it gently over the spoon. The two liqueurs should form distinct layers. Shoot or sip slowly to enjoy the layered effect."}'::jsonb, 'Shot glass', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_BUTTERSCOTCH_SCHNAPPS"}', '{"creamy","sweet","buttery","caramel"}', 17, 150, 'easy', 30, '{"milk"}', '{"lactose","alcohol"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"party","celebration","casual"}', false, false, '{"slippery-nipple","comfortable-screw","buttery-finger"}', 'Very popular party shot. Layering technique is important for visual appeal - pour slowly. Can add vodka on bottom for "Slippery Nipple" variation. Very sweet - warn guests. Name may require discretion with certain clientele.', 'budget', 70, 'https://www.liquor.com/recipes/buttery-nipple/', 'Classic 1980s layered shooter, popular at parties.', 1)
