-- 132. Carajillo
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-f6a7-4b8c-9d0e-1f2a3b4c5d6e', 'carajillo', 'e7f6d5c4b3a2918273645546372819202122232425', '{"en":"Carajillo"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","coffee","spanish","digestif"}', '{"en":"A beloved Spanish cocktail that combines espresso with Licor 43, a vanilla-citrus liqueur. Simple yet sophisticated, the Carajillo is traditionally served after meals as a digestif, offering the perfect balance of coffee bitterness and sweet, aromatic vanilla notes."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Barcelona","country":"Spain"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Carajillo has roots in 19th century Cuba, where Spanish soldiers would add rum to coffee for \"coraje\" (courage) before battle. The name evolved to \"carajillo.\" The modern version using Licor 43 became popular in Spain in the 1960s, particularly in Barcelona and Valencia. Licor 43, with its vanilla and citrus notes, proved to be the perfect complement to espresso, creating a drink that''s both energizing and relaxing."},"named_after":{"en":"Derived from \"coraje\" (courage in Spanish), referring to the drink''s original purpose of providing courage to soldiers."}}'::jsonb, '{"profile":["coffee","sweet","vanilla","citrus"],"description":{"en":"Rich espresso bitterness harmonizes beautifully with Licor 43''s sweet vanilla and subtle citrus notes. The result is smooth, aromatic, and perfectly balanced between bitter and sweet. The liqueur adds a silky texture and complex botanical undertones."},"first_impression":{"en":"Sweet vanilla and espresso hit simultaneously, warm and inviting"},"finish":{"en":"Long, warming finish with lingering coffee and vanilla notes"},"balance":{"en":"Perfectly balanced - espresso bitterness prevents excessive sweetness from the liqueur"}}'::jsonb, '{"best_time":["afternoon","evening","late_night"],"occasions":["digestivo","after_dinner","coffee_break","casual"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Excellent with churros, flan, crème brûlée, or any vanilla-based dessert. Also pairs well with dark chocolate, almond cookies, and Spanish pastries like ensaimadas."},"ideal_for":{"en":"Perfect for coffee lovers who enjoy after-dinner drinks. Ideal for those who want the pick-me-up of espresso with a touch of sweetness. Great for anyone exploring Spanish drinking culture."}}'::jsonb, '[{"ingredient_id":"ING_LICOR_43","quantity":{"amount":45,"unit":"ml"},"display_name":"Licor 43","notes":null},{"ingredient_id":"ING_ESPRESSO","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh espresso","notes":null}]'::jsonb, 'build', '{"en":"Pour Licor 43 into a rocks glass filled with ice. Brew a fresh espresso shot and pour it over the liqueur. Stir gently to combine. Garnish with coffee beans."}'::jsonb, 'Rocks glass', '{"en":"Coffee beans (3 beans traditional)"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_LICOR_43"}', '{"coffee","sweet","vanilla","citrus"}', 18, 180, 'easy', 90, '{"sulphites","caffeine"}', '{"alcohol","caffeine_intolerance","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"digestivo","after_dinner","coffee_break","casual"}', false, false, '{"carajillo-with-brandy","carajillo-with-rum","iced-carajillo"}', 'Espresso must be fresh and hot. Can be served hot or over ice. Traditional Spanish serve includes 3 coffee beans as garnish. Some regions flame the liqueur before adding espresso for theatrical presentation.', 'mid', 75, 'https://www.licor43.com/en/cocktails/carajillo', 'Traditional Spanish recipe, popularized internationally in recent years.', 1)
