-- 133. Chi Chi
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('8c3f1a9e-4d2b-5e7c-9a1f-3b6d8e2c4a7f', 'chi-chi', 'chi-chi-tiki-tropical-famous-2025', '{"en":"Chi Chi"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","vodka","pina-colada-variation","creamy","frozen"}', '{"en":"The vodka version of the classic Piña Colada, blending vodka with pineapple juice and cream of coconut. The Chi Chi offers the same creamy, tropical indulgence as its rum-based cousin but with a cleaner, more neutral spirit base."}'::jsonb, '{"created_year":"1970s","origin":{"city":"Various","bar":"Multiple Claims","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Chi Chi emerged in the 1970s as bartenders began experimenting with vodka variations of classic cocktails during vodka''s rise to popularity in America. While the exact origin is disputed, the drink became popular in beach bars and resorts as a lighter alternative to the Piña Colada. The name \"Chi Chi\" is playful and catchy, though its exact etymology is unclear. Some suggest it was named after a socialite, while others believe it''s simply a fun, memorable name that matched the drink''s festive character."},"named_after":{"en":"The exact origin of the name \"Chi Chi\" is unclear, though it may reference a socialite or simply be a playful, memorable name chosen for its festive sound."}}'::jsonb, '{"profile":["creamy","tropical","sweet"],"description":{"en":"Creamy and luscious with bright pineapple and rich coconut flavors. The vodka provides a clean, neutral base that lets the tropical fruits shine. Smooth, indulgent, and dangerously easy to drink."},"first_impression":{"en":"Sweet pineapple and creamy coconut hit simultaneously with smooth texture"},"finish":{"en":"Long, creamy finish with lingering coconut and tropical fruit notes"},"balance":{"en":"Well-balanced between sweet and creamy - the pineapple acidity prevents it from being too rich"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","vacation","celebration","summer_gathering"],"seasons":["summer","year_round"],"food_pairings":{"en":"Pairs beautifully with grilled seafood, coconut shrimp, tropical fruit platters, Hawaiian pizza, or Caribbean jerk dishes. Also excellent with Thai and tropical Asian cuisine."},"ideal_for":{"en":"Perfect for beach vacations, poolside lounging, and tropical-themed parties. Ideal for those who love creamy cocktails but prefer vodka over rum. A great frozen drink for hot summer days."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":60,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_CREAM_OF_COCONUT","quantity":{"amount":30,"unit":"ml"},"display_name":"Cream of coconut","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Fresh pineapple juice","notes":null}]'::jsonb, 'blend', '{"en":"Add all ingredients to a blender with 1 cup of ice. Blend until smooth and creamy. Pour into a hurricane glass. Garnish with pineapple wedge and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'blended', 'frozen', '{"ING_VODKA"}', '{"creamy","tropical","sweet"}', 12, 280, 'easy', 90, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegetarian","gluten-free"}', '{"summer","year_round"}', '{"beach","pool_party","vacation","celebration"}', false, false, '{"pina-colada","virgin-chi-chi","strawberry-chi-chi"}', 'Vodka version of Piña Colada. Use Coco López or similar cream of coconut (not coconut milk). Blend until smooth - consistency should be like a frozen dessert. Fresh pineapple juice highly recommended. Can also be served shaken over ice if blender unavailable.', 'mid', 65, 'https://www.liquor.com/recipes/chi-chi/', 'Vodka variation of classic Piña Colada. Multiple origin claims.', 1)
