-- 140. Diamondback
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e1d7f2c6-8a5b-7e4c-2b9d-5a6b7c8d9e0f', 'diamondback', 'b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7', '{"en":"Diamondback"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","rye","apple","spirit-forward"}', '{"en":"A sophisticated cocktail combining rye whiskey, applejack, and yellow Chartreuse. The Diamondback is a bold, spirit-forward drink with complex herbal and apple notes balanced by spicy rye."}'::jsonb, '{"created_year":"1951","origin":{"city":"Baltimore","bar":"Lord Baltimore Hotel","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Diamondback was created at Baltimore''s Lord Baltimore Hotel in 1951. Named after the diamondback terrapin, Maryland''s state reptile, this cocktail celebrates local ingredients - particularly applejack, which was popular in the Mid-Atlantic region. The drink gained renewed attention during the modern craft cocktail renaissance."},"named_after":{"en":"Named after the diamondback terrapin, Maryland''s state reptile, celebrating the cocktail''s Baltimore heritage."}}'::jsonb, '{"profile":["spirit-forward","complex","herbal"],"description":{"en":"Bold and complex with rye''s spicy backbone, applejack''s fruity depth, and Chartreuse''s distinctive herbal notes. Spirit-forward yet balanced, with layers of flavor unfolding with each sip."},"first_impression":{"en":"Spicy rye and apple notes hit first, followed by herbal Chartreuse complexity"},"finish":{"en":"Long, warming finish with lingering spice, apple, and herbal notes"},"balance":{"en":"Perfectly balanced trinity - rye spice, apple fruit, and herbal complexity in harmony"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["aperitivo","digestivo","date_night","special_occasion"],"seasons":["autumn","winter"],"food_pairings":{"en":"Excellent with rich meats, aged cheeses, roasted pork, or apple-based desserts. Perfect as a pre-dinner drink or after a hearty meal."},"ideal_for":{"en":"Perfect for whiskey lovers who appreciate complex, spirit-forward cocktails. An excellent choice for those seeking sophisticated alternatives to the Manhattan."}}'::jsonb, '[{"ingredient_id":"ING_RYE_WHISKEY","quantity":{"amount":45,"unit":"ml"},"display_name":"Rye whiskey","notes":null},{"ingredient_id":"ING_APPLEJACK","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Applejack","notes":null},{"ingredient_id":"ING_YELLOW_CHARTREUSE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Yellow Chartreuse","notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir gently for 30-40 seconds until well-chilled. Strain into a chilled coupe or cocktail glass. Garnish with a lemon twist, expressing the oils over the drink."}'::jsonb, 'Coupe glass', '{"en":"Lemon twist"}'::jsonb, 'none', 'up', '{"ING_RYE_WHISKEY"}', '{"spirit-forward","complex","herbal"}', 32, 180, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"aperitivo","digestivo","date_night","special_occasion"}', false, false, '{"improved-diamondback"}', 'Use bonded applejack (Laird''s Bottled in Bond) for best results. Yellow Chartreuse is essential - do not substitute green. The 2:1:1 ratio creates perfect balance. Stir well to achieve proper dilution.', 'premium', 55, 'https://www.diffordsguide.com/cocktails/recipe/1276/diamondback', 'Created at Lord Baltimore Hotel, 1951. Celebrates Maryland''s diamondback terrapin and local applejack tradition.', 1)
