-- 143. Electric Lemonade
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b0c1d2e3-4567-8901-b456-890123456789', 'electric-lemonade', 'f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4f5a6', '{"en":"Electric Lemonade"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","vodka","blue","lemonade","refreshing","party"}', '{"en":"A vibrant electric blue cocktail combining vodka, blue curaçao, and lemonade with lemon-lime soda. The Electric Lemonade is a visually stunning, refreshing drink that''s become a pool party and summer favorite."}'::jsonb, '{"created_year":"1990","origin":{"city":"United States","country":"USA"},"story":{"en":"The Electric Lemonade emerged in the 1990s as bars and clubs sought visually striking cocktails that would stand out. The electric blue color from blue curaçao combined with the familiar taste of lemonade created an instant hit. Unlike the dangerously strong \"iced tea\" family, the Electric Lemonade is more approachable with moderate alcohol content. It became particularly popular at beach bars, pool parties, and summer festivals where its bright blue color photographs well and evokes tropical vibes. The drink represents the 1990s trend of colorful, Instagram-worthy cocktails before Instagram existed."},"named_after":{"en":"Named for its electric blue color and lemonade base, creating an eye-catching, energetic appearance."}}'::jsonb, '{"profile":["sweet","citrus","lemon","orange"],"description":{"en":"Sweet and citrusy with dominant lemon flavor from the lemonade, complemented by blue curaçao''s orange notes. The vodka provides a clean alcoholic base while the lemon-lime soda adds refreshing effervescence. Tastes like electric blue lemonade with a gentle kick."},"first_impression":{"en":"Sweet lemonade with electric blue color and citrus brightness"},"finish":{"en":"Clean, refreshing finish with lingering lemon and orange notes"},"balance":{"en":"Well-balanced sweetness with good citrus brightness - refreshing without being cloying"}}'::jsonb, '{"best_time":["afternoon","evening","summer"],"occasions":["party","pool_party","beach","celebration","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with summer party foods, grilled items, seafood, fish tacos, and light appetizers. Great poolside drink with chips and salsa."},"ideal_for":{"en":"Perfect for pool parties, beach bars, and summer celebrations. Great for those who love visually striking drinks and sweet, refreshing flavors. Popular with Instagram-loving crowds."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_BLUE_CURACAO","quantity":{"amount":15,"unit":"ml"},"display_name":"Blue curaçao","notes":null},{"ingredient_id":"ING_LEMONADE","quantity":{"amount":60,"unit":"ml"},"display_name":"Lemonade","notes":null},{"ingredient_id":"ING_LEMON_LIME_SODA","quantity":{"amount":30,"unit":"ml"},"display_name":"Lemon-lime soda (7-Up or Sprite)","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour vodka and blue curaçao over ice. Add lemonade and top with lemon-lime soda. Stir gently to create the electric blue color. Garnish with a lemon wheel."}'::jsonb, 'Highball glass', '{"en":"Lemon wheel and cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"sweet","citrus","lemon","orange"}', 12, 215, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","pool_party","beach","celebration"}', false, false, '{"blue-lagoon","electric-iced-tea"}', 'The electric blue color is the signature feature - make sure it''s vibrant. Can use homemade or store-bought lemonade. Very photogenic drink - popular on social media.', 'low', 80, 'https://www.diffordsguide.com/cocktails/recipe/1253/electric-lemonade', '1990s creation, popular at pool parties and beach bars for its electric blue color.', 1)
