-- 145. Fireball Shot
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('i9j0k1l2-3456-7890-ij90-kl12mn345678', 'fireball-shot', 'fireball_shot_famous_cinnamon_whisky_spicy', '{"en":"Fireball Shot"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","cinnamon","whisky","spicy","party"}', '{"en":"A straight shot of Fireball Cinnamon Whisky, the iconic spicy-sweet liqueur that became a cultural phenomenon. This simple but intense shot delivers powerful cinnamon candy flavor with a fiery kick that lives up to its name."}'::jsonb, '{"created_year":"1984","origin":{"city":"Unknown","bar":"Seagram Company","country":"Canada"},"story":{"en":"Fireball Cinnamon Whisky was originally developed in Canada in 1984 by Seagram as \"Dr. McGillicuddy''s Fireball Whisky.\" It was rebranded as simply \"Fireball\" and exploded in popularity in the United States during the 2010s, becoming one of the best-selling liqueurs in America. The drink became synonymous with party culture and college nightlife, spawning countless social media moments and drinking traditions."},"named_after":{"en":"Named \"Fireball\" for its intense cinnamon heat and spicy flavor that feels like a fireball going down."}}'::jsonb, '{"profile":["spicy","sweet","cinnamon"],"description":{"en":"Intensely sweet with dominant cinnamon candy flavor reminiscent of Red Hots or Big Red gum. The whisky base provides warmth while the cinnamon creates a burning sensation. Syrupy sweet with a fiery spice kick."},"first_impression":{"en":"Intense cinnamon candy sweetness with immediate heat"},"finish":{"en":"Long, burning cinnamon finish with lingering sweetness"},"balance":{"en":"Very sweet with intense cinnamon spice - polarizing flavor"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","nightlife","tailgate","celebration","college_party"],"seasons":["autumn","winter"],"food_pairings":{"en":"Best as a standalone shot. Can be paired with apple-based desserts or cinnamon treats. Popular with pizza and wings at casual gatherings."},"ideal_for":{"en":"Perfect for those who love sweet, spicy shots and cinnamon candy flavor. A party staple and college favorite. Extremely polarizing - people either love it or hate it."}}'::jsonb, '[{"ingredient_id":"ING_FIREBALL_WHISKY","quantity":{"amount":45,"unit":"ml"},"display_name":"Fireball Cinnamon Whisky","notes":null}]'::jsonb, 'pour', '{"en":"Pour Fireball Cinnamon Whisky directly into a shot glass. Serve immediately. Can be served chilled or at room temperature."}'::jsonb, 'Shot glass', '{"en":"None (sometimes served with cinnamon sugar rim)"}'::jsonb, 'none', 'shot', '{"ING_FIREBALL_WHISKY"}', '{"spicy","sweet","cinnamon"}', 33, 108, 'easy', 10, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 3, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"party","nightlife","celebration"}', false, false, '{"fireball-apple-cider","cinnamon-toast-crunch-shot","angry-balls"}', 'Store in freezer for smoother shots - the cold reduces the burn. Extremely popular but polarizing. Some customers prefer it mixed with apple cider or ginger ale. Check ID carefully as this is often requested by younger drinkers.', 'low', 95, 'https://www.fireballwhisky.com/', 'Iconic cinnamon whisky that became a cultural phenomenon in the 2010s.', 1)
