-- 146. Fitzgerald
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d4e5f6a7-b8c9-4d0e-1f2a-3b4c5d6e7f8a', 'fitzgerald', 'c5d4e3f2a1b0918273645546372819202122232425', '{"en":"Fitzgerald"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","gin","sour","prohibition"}', '{"en":"A refined gin sour with a touch of Angostura bitters, the Fitzgerald is essentially an elevated Tom Collins served straight up. Named after F. Scott Fitzgerald, this modern classic balances juniper-forward gin with fresh lemon and simple syrup, with bitters adding aromatic complexity."}'::jsonb, '{"created_year":"2000s","origin":{"city":"San Francisco","bar":"Bourbon & Branch","country":"USA"},"creator":{"name":"Dale DeGroff","profession":"bartender"},"story":{"en":"The Fitzgerald was created by legendary bartender Dale DeGroff during the cocktail renaissance of the early 2000s. DeGroff, known as \"King Cocktail,\" designed it as a tribute to F. Scott Fitzgerald and the Jazz Age. The drink reimagines the Tom Collins by serving it up rather than tall, and adding Angostura bitters for depth. It represents the modern craft cocktail movement''s approach to classic drinks: respecting tradition while refining and elevating the formula."},"named_after":{"en":"Named after F. Scott Fitzgerald, the famous American novelist and author of \"The Great Gatsby,\" representing the Jazz Age and Prohibition era."}}'::jsonb, '{"profile":["citrus","botanical","balanced","aromatic"],"description":{"en":"Bright, refreshing, and beautifully balanced. London Dry gin provides juniper and botanical notes, fresh lemon adds tartness, simple syrup brings sweetness, and Angostura bitters contribute aromatic spice and complexity. Clean, crisp, and sophisticated."},"first_impression":{"en":"Bright lemon and juniper hit first, aromatic bitters follow"},"finish":{"en":"Clean, crisp finish with lingering gin botanicals and bitter notes"},"balance":{"en":"Perfectly balanced sour - sweet, tart, and botanical in harmony"}}'::jsonb, '{"best_time":["evening","afternoon","cocktail_hour"],"occasions":["aperitivo","date_night","celebration","literary_event"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with seafood appetizers, oysters, ceviche, or light salads. Also pairs well with goat cheese, smoked salmon, and citrus-based dishes."},"ideal_for":{"en":"Perfect for gin lovers who appreciate classic sours. Ideal for those who enjoy Tom Collins but want something more refined and spirit-forward. Great for literary enthusiasts and fans of the Jazz Age."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"London Dry Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dashes"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a chilled coupe or cocktail glass. Garnish with a lemon wheel."}'::jsonb, 'Coupe glass', '{"en":"Lemon wheel"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","botanical","balanced","aromatic"}', 18, 160, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","celebration","literary_event"}', false, false, '{"elderflower-fitzgerald","pink-fitzgerald"}', 'Use fresh lemon juice only. Some versions use 2:1:1 gin-lemon-syrup ratio for more spirit-forward profile. Bitters are essential - don''t skip. Can serve in rocks glass over ice as alternative presentation.', 'mid', 70, 'https://www.liquor.com/recipes/fitzgerald/', 'Created by Dale DeGroff in the early 2000s during the craft cocktail renaissance.', 1)
