-- 151. Gin Rickey
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c5d6e7f8-9012-3456-c901-345678901234', 'gin-rickey', 'a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1', '{"en":"Gin Rickey"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","gin","lime","refreshing","classic","unsweetened"}', '{"en":"A refreshingly simple and unsweetened highball combining gin, fresh lime, and club soda. The Gin Rickey is one of the earliest American cocktails, prized for its crisp, clean taste without any added sugar."}'::jsonb, '{"created_year":"1883","origin":{"city":"Washington, D.C.","bar":"Shoomaker''s Bar","country":"USA"},"creator":{"name":"George A. Williamson","profession":"bartender"},"story":{"en":"The Gin Rickey was created in 1883 by bartender George A. Williamson at Shoomaker''s Bar in Washington, D.C. It was named after Colonel Joe Rickey, a Democratic lobbyist who frequented the bar. Originally made with bourbon, Colonel Rickey preferred it with gin, and that version became the classic. The Rickey was one of the first cocktails to use carbonated water and became extremely popular in the late 19th and early 20th centuries, particularly during hot Washington summers. It''s notable for being unsweetened, making it refreshing without cloying."},"named_after":{"en":"Named after Colonel Joe Rickey, a Democratic lobbyist and regular patron at Shoomaker''s Bar."}}'::jsonb, '{"profile":["tart","refreshing","citrus","crisp"],"description":{"en":"Crisp, tart, and exceptionally refreshing with no sweetness whatsoever. The gin''s botanicals shine through, complemented by fresh lime tartness and clean carbonation. It''s bracingly dry and perfect for those who dislike sweet drinks."},"first_impression":{"en":"Tart lime and gin botanicals with crisp effervescence"},"finish":{"en":"Dry, clean finish with lingering lime and juniper notes"},"balance":{"en":"Perfectly balanced tartness - dry without being harsh, refreshing without sweetness"}}'::jsonb, '{"best_time":["afternoon","evening","summer"],"occasions":["casual","aperitivo","garden_party","daytime"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent aperitif before meals. Pairs well with oysters, fresh seafood, ceviche, salads, and light appetizers. Perfect with Vietnamese or Thai cuisine."},"ideal_for":{"en":"Perfect for those who dislike sweet cocktails and prefer dry, refreshing drinks. Great for hot weather, health-conscious drinkers (no added sugar), or anyone who appreciates gin''s botanical character."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_CLUB_SODA","quantity":{"amount":90,"unit":"ml"},"display_name":"Club soda","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour gin and fresh lime juice over ice. Top with club soda. Stir gently once. Squeeze lime half into drink and drop the spent shell in."}'::jsonb, 'Highball glass', '{"en":"Spent lime half"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_GIN"}', '{"tart","refreshing","citrus","crisp"}', 12, 110, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free","low_sugar","keto"}', 0, '{"vegan","gluten-free","dairy-free","low-sugar"}', '{"spring","summer"}', '{"casual","aperitivo","garden_party"}', false, false, '{"bourbon-rickey","vodka-rickey","rum-rickey"}', 'The hallmark of a Rickey is NO SUGAR - don''t add simple syrup. Use fresh lime, never bottled. Originally made with bourbon (ask if customer wants original version). Drop spent lime half into drink - it''s traditional.', 'low', 55, 'https://www.diffordsguide.com/cocktails/recipe/1248/gin-rickey', 'Created 1883 by George A. Williamson at Shoomaker''s Bar, Washington D.C.', 1)
