-- 158. Hurricane
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('1a2b3c4d-5e6f-7a8b-9c0d-1e2f3a4b5c6d', 'hurricane', 'hurricane_famous_tiki_tropical_cocktail', '{"en":"Hurricane"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","fruity","strong"}', '{"en":"The Hurricane is New Orleans'' most famous cocktail, a potent rum punch combining light and dark rum with passion fruit syrup and citrus. Named after the hurricane lamp-shaped glass it''s served in, this sweet and strong drink is a French Quarter institution."}'::jsonb, '{"created_year":"1940","origin":{"city":"New Orleans","bar":"Pat O''Brien''s Bar","country":"USA"},"creator":{"name":"Pat O''Brien","profession":"bar owner"},"story":{"en":"The Hurricane was created in the 1940s by Pat O''Brien at his eponymous French Quarter bar in New Orleans. During World War II, whiskey was scarce but rum was plentiful, so O''Brien created this potent rum cocktail to use up his surplus stock. He served it in a hurricane lamp-shaped glass, and the drink quickly became a hit with sailors on shore leave. The cocktail became synonymous with New Orleans and Bourbon Street nightlife, remaining one of the city''s signature drinks to this day."},"named_after":{"en":"Named after the hurricane lamp-shaped glass in which it is traditionally served, with its distinctive curved design."}}'::jsonb, '{"profile":["fruity","sweet","strong"],"description":{"en":"Bold and tropical with intense passion fruit flavor dominating the palate. The Hurricane combines sweet fruitiness with the warming strength of double rum, balanced by bright citrus notes. Deceptively strong despite its sweet, approachable taste."},"first_impression":{"en":"Intense passion fruit sweetness hits first, masking the strong rum foundation underneath"},"finish":{"en":"Long, warming finish with rum heat and lingering tropical fruit notes"},"balance":{"en":"Sweet and strong with fruit flavors masking significant alcohol content - dangerously drinkable"}}'::jsonb, '{"best_time":["afternoon","evening","late_night"],"occasions":["mardi_gras","party","celebration","festival"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with New Orleans classics like jambalaya, gumbo, po'' boys, or beignets. Also pairs well with Cajun and Creole cuisine, fried seafood, and spicy dishes."},"ideal_for":{"en":"Perfect for party lovers and those seeking a festive, high-energy drink. Ideal for Mardi Gras celebrations or anyone wanting to experience New Orleans nightlife. Great for those who enjoy sweet, fruity cocktails with a serious kick."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_SYRUP","quantity":{"amount":60,"unit":"ml"},"display_name":"Passion fruit syrup","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a hurricane glass filled with ice. Garnish with orange slice and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Orange slice, maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_WHITE","ING_RUM_DARK"}', '{"fruity","sweet","strong"}', 14, 280, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"mardi_gras","party","celebration","festival"}', false, false, '{"new-orleans-hurricane","french-quarter-hurricane"}', 'Original Pat O''Brien''s recipe is proprietary, but this is the classic formula. Can substitute passion fruit purée for syrup. Glass should be hurricane lamp-shaped. Very strong - warn guests it''s deceptively potent.', 'mid', 90, 'https://www.patobriens.com/hurricane-drink', 'Based on classic New Orleans formula. Historical information from Pat O''Brien''s Bar and New Orleans cocktail archives.', 1)
