-- 161. Irish Car Bomb
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('h8i9j0k1-2345-6789-hi89-jk01lm234567', 'irish-car-bomb', 'irish_car_bomb_shot_famous_guinness_baileys_bomb', '{"en":"Irish Car Bomb"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","bomb","beer","irish","creamy","st-patricks"}', '{"en":"A bomb shot combining Irish whiskey and Baileys Irish Cream dropped into Guinness stout. This potent drink must be consumed quickly before the cream curdles. NOTE: The name is considered offensive in Ireland due to its reference to The Troubles; alternative names include \"Irish Slammer\" or \"Dublin Drop.\""}'::jsonb, '{"created_year":"1979","origin":{"city":"Norwich","bar":"Wilson''s Saloon","country":"USA"},"creator":{"name":"Charles Burke Cronin Oat","profession":"bartender"},"story":{"en":"Created in 1979 by bartender Charles Oat at Wilson''s Saloon in Norwich, Connecticut. While popular in America, especially around St. Patrick''s Day, the drink''s name is highly controversial and considered deeply offensive in Ireland due to its reference to the car bombings during The Troubles (1960s-1990s). Many Irish pubs refuse to serve it, and bartenders recommend using alternative names like \"Irish Slammer\" or simply avoiding the drink in Irish establishments."},"named_after":{"en":"CONTROVERSIAL: Named after car bomb attacks during The Troubles in Northern Ireland. This name is considered highly offensive in Ireland and should be avoided or replaced with \"Irish Slammer\" or \"Dublin Drop.\""}}'::jsonb, '{"profile":["creamy","malty","sweet","boozy"],"description":{"en":"Rich and creamy with chocolate and coffee notes from Baileys, whiskey warmth, and the roasted malt character of Guinness. Creates a surprisingly smooth combination when drunk quickly."},"first_impression":{"en":"Creamy sweetness meets roasted stout bitterness"},"finish":{"en":"Rich, creamy finish with lingering whiskey warmth"},"balance":{"en":"Sweet cream balances bitter stout - must drink quickly before curdling"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","st_patricks_day","celebration"],"seasons":["all_seasons"],"food_pairings":{"en":"Not typically paired with food. Best consumed as a standalone drink. If serving around St. Patrick''s Day, pairs with Irish pub food like shepherd''s pie or fish and chips."},"ideal_for":{"en":"Popular in American bars around St. Patrick''s Day. WARNING: Highly offensive in Ireland - use alternative names \"Irish Slammer\" or \"Dublin Drop\" or avoid entirely in Irish establishments. Must be consumed immediately."}}'::jsonb, '[{"ingredient_id":"ING_IRISH_WHISKEY","quantity":{"amount":30,"unit":"ml"},"display_name":"Irish Whiskey","notes":null},{"ingredient_id":"ING_BAILEYS","quantity":{"amount":30,"unit":"ml"},"display_name":"Baileys Irish Cream","notes":null},{"ingredient_id":"ING_GUINNESS","quantity":{"amount":250,"unit":"ml"},"display_name":"Guinness Stout","notes":null}]'::jsonb, 'build', '{"en":"Fill a pint glass 3/4 full with Guinness. In a shot glass, add Irish whiskey and top with Baileys. Drop the shot glass into the pint glass and drink immediately before the Baileys curdles."}'::jsonb, 'Shot glass (dropped into pint glass)', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_IRISH_WHISKEY","ING_BAILEYS"}', '{"creamy","malty","sweet","boozy"}', 8, 250, 'easy', 45, '{"milk","sulphites","gluten"}', '{"lactose","alcohol","sulphites_intolerance","gluten_intolerance"}', '{"vegetarian","pescatarian"}', 0, '{"vegetarian"}', '{"all-seasons"}', '{"party","celebration","st-patricks-day"}', false, false, '{"irish-slammer","dublin-drop","irish-bomb"}', 'CULTURAL SENSITIVITY WARNING: This drink''s name is highly offensive in Ireland. Recommend using "Irish Slammer" or "Dublin Drop" as alternative names. NEVER serve this in Irish pubs or to Irish customers. Must be consumed immediately as Baileys will curdle in Guinness. Use fresh Guinness and serve quickly.', 'mid', 65, 'https://en.wikipedia.org/wiki/Irish_Car_Bomb', 'Controversial bomb shot created in 1979. Name is offensive in Ireland - use alternatives.', 1)
