-- 162. Irish Cream Coffee
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e1f2a3b4-c5d6-4e7f-8a9b-0c1d2e3f4a5b', 'irish-cream-coffee', 'irish_cream_coffee_baileys_hot_coffee', '{"en":"Irish Cream Coffee"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","coffee","hot","sweet"}', '{"en":"A warming, indulgent hot coffee cocktail made with Irish cream liqueur and topped with whipped cream. This creamy alternative to Irish Coffee offers a sweeter, more dessert-like experience perfect for cold evenings."}'::jsonb, '{"created_year":"1970s","origin":{"country":"Ireland"},"story":{"en":"Irish Cream Coffee emerged in the 1970s following the creation and commercial success of Baileys Irish Cream in 1974. As Baileys became popular worldwide, bartenders and home enthusiasts began adding it to hot coffee as a simpler, sweeter alternative to the classic Irish Coffee (which uses whiskey, coffee, sugar, and cream). The drink became especially popular in cafes and restaurants as an after-dinner beverage, offering a more accessible entry point to spiked coffee drinks. It remains a favorite winter warmer and dessert coffee option."},"named_after":{"en":"Named for its primary ingredient, Irish cream liqueur, combined with coffee."}}'::jsonb, '{"profile":["creamy","sweet","coffee","chocolate"],"description":{"en":"Rich, sweet coffee with pronounced chocolate and vanilla notes from the Irish cream. Creamy and indulgent with a perfect balance of coffee bitterness and liqueur sweetness. Warmer and more dessert-like than traditional Irish Coffee."},"first_impression":{"en":"Hot, sweet coffee with immediate creamy chocolate notes"},"finish":{"en":"Warm, lingering sweetness with coffee and cream"},"balance":{"en":"Sweet and creamy with enough coffee bitterness to prevent it from being cloying"}}'::jsonb, '{"best_time":["morning","afternoon","evening"],"occasions":["brunch","dessert","winter","casual","nightcap"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect with breakfast pastries, chocolate desserts, tiramisu, or as a dessert replacement. Also pairs well with biscotti, cookies, and chocolate cake."},"ideal_for":{"en":"Perfect for coffee and Irish cream lovers seeking a warming dessert drink. Ideal for cold weather, après-ski, brunch occasions, and anyone who wants a sweeter, easier alternative to Irish Coffee."}}'::jsonb, '[{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":45,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null},{"ingredient_id":"ING_HOT_COFFEE","quantity":{"amount":120,"unit":"ml"},"display_name":"Hot coffee","notes":null},{"ingredient_id":"ING_WHIPPED_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Whipped cream","optional":true,"notes":null}]'::jsonb, 'build', '{"en":"Warm an Irish coffee glass or heat-resistant mug with hot water, then discard. Pour Irish cream into the warmed glass. Add freshly brewed hot coffee and stir gently. Top with a generous dollop of whipped cream. Optionally dust with cocoa powder or chocolate shavings. Serve immediately while hot."}'::jsonb, 'Irish coffee glass (or mug)', '{"en":"Whipped cream, cocoa powder dusting, or chocolate shavings"}'::jsonb, 'none', 'hot', '{"ING_IRISH_CREAM"}', '{"creamy","sweet","coffee","chocolate"}', 8, 220, 'easy', 45, '{"milk"}', '{"lactose","alcohol","caffeine"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter"}', '{"brunch","dessert","casual","nightcap"}', false, false, '{"irish-coffee","nutty-irishman","mudslide-coffee"}', 'Simpler and sweeter than traditional Irish Coffee. Use fresh, hot coffee for best results. Can add extra shot of espresso for stronger coffee flavor. Popular as a dessert coffee option. Baileys is the preferred Irish cream brand.', 'budget', 80, 'https://www.baileys.com/en-us/recipes/baileys-coffee/', 'Popular hot coffee drink made with Baileys Irish Cream.', 1)
