-- 169. Last Rites
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a1b2c3d4-e5f6-4a7b-8c9d-0e1f2a3b4c5d', 'last-rites', 'f8e7d6c5b4a39281706958473625140fabcdef01', '{"en":"Last Rites"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","spirit-forward","herbal","bitter"}', '{"en":"A spirit-forward cocktail featuring mezcal, green Chartreuse, maraschino liqueur, and lime. The Last Rites is a smoky, herbal, and complex drink that balances agave spirits with French liqueurs."}'::jsonb, '{"created_year":"2004","origin":{"city":"New York City","bar":"Death & Co","country":"USA"},"creator":{"name":"Phil Ward","profession":"bartender"},"story":{"en":"Created by Phil Ward at Death & Co in New York City in 2004, the Last Rites is a bold variation on the classic Last Word. Ward substituted mezcal for gin, creating a smoky, agave-driven interpretation that became one of the signature cocktails of the modern craft cocktail movement."},"named_after":{"en":"A play on \"Last Word\" - the name references both the final sacrament and the smoky, intense character of mezcal."}}'::jsonb, '{"profile":["smoky","herbal","complex"],"description":{"en":"Smoky and herbal with layers of complexity. The mezcal provides an earthy, smoky backbone, while green Chartreuse adds herbal intensity. Maraschino brings subtle sweetness and cherry notes, balanced by bright lime acidity."},"first_impression":{"en":"Smoky mezcal and herbal Chartreuse hit immediately, followed by sweet-tart complexity"},"finish":{"en":"Long, herbal finish with lingering smoke and cherry notes"},"balance":{"en":"Perfectly balanced between smoky spirits and herbal liqueurs, with lime providing essential brightness"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["cocktail_hour","date_night","special_occasion"],"seasons":["all_seasons"],"food_pairings":{"en":"Pairs excellently with grilled meats, Mexican cuisine, barbecue, and strong cheeses. The smoky character complements charred and roasted flavors."},"ideal_for":{"en":"Perfect for adventurous drinkers who appreciate complex, spirit-forward cocktails. Ideal for mezcal lovers and those seeking something bold and herbal."}}'::jsonb, '[{"ingredient_id":"ING_MEZCAL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Mezcal","notes":null},{"ingredient_id":"ING_CHARTREUSE_GREEN","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Green Chartreuse","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Maraschino liqueur","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe glass. No garnish needed."}'::jsonb, 'Coupe', '{"en":"None"}'::jsonb, 'none', 'up', '{"ING_MEZCAL"}', '{"smoky","herbal","complex"}', 28, 195, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-seasons"}', '{"cocktail_hour","date_night","special_occasion"}', false, false, '{"last-word","final-ward"}', 'Use quality mezcal with good smoke character. Shake hard to properly chill and dilute the strong spirits. This is a variation on the Last Word - offer comparison for curious guests.', 'premium', 72, 'https://deathandcompany.com', 'Created by Phil Ward at Death & Co, 2004. Modern classic cocktail.', 1)
