-- 170. Lemon Drop Shot
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c3d4e5f6-7890-1234-cd34-ef56gh789012', 'lemon-drop-shot', 'lemon_drop_shot_famous_citrus_sweet', '{"en":"Lemon Drop Shot"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","citrus","vodka","sweet","sour"}', '{"en":"A sweet and sour vodka-based shooter with fresh lemon juice and sugar. Named after the popular lemon drop candy, this shot delivers a perfect balance of tartness and sweetness with a sugared rim."}'::jsonb, '{"created_year":"1970s","origin":{"city":"San Francisco","bar":"Henry Africa''s","country":"USA"},"story":{"en":"The Lemon Drop Shot was created in the 1970s at Henry Africa''s bar in San Francisco. It was inspired by the classic lemon drop candy and quickly became a popular choice for those seeking a sweet-tart shooter. The drink perfectly captures the nostalgic flavor of the candy it''s named after."},"named_after":{"en":"Named after the lemon drop candy, known for its sweet-tart flavor profile."}}'::jsonb, '{"profile":["sweet","sour","citrus"],"description":{"en":"Bright lemon flavor with balanced sweetness. The sugar rim provides instant sweetness, followed by tart lemon and smooth vodka. Refreshing and candy-like."},"first_impression":{"en":"Sweet sugar rim followed by tart lemon burst"},"finish":{"en":"Clean, refreshing finish with lingering lemon sweetness"},"balance":{"en":"Perfectly balanced sweet and sour, true to the candy inspiration"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","girls_night","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs well with light appetizers, seafood dishes, or lemon-based desserts. Great as a palate cleanser between courses."},"ideal_for":{"en":"Perfect for those who enjoy sweet, citrus-forward shots. A crowd-pleaser that appeals to both casual drinkers and cocktail enthusiasts."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":30,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple Syrup","notes":null},{"ingredient_id":"ING_SUGAR","quantity":{"amount":1,"unit":"teaspoon"},"display_name":"Sugar (for rim)","notes":null}]'::jsonb, 'shake', '{"en":"Rim the shot glass with sugar by moistening the rim with lemon juice and dipping in sugar. Add vodka, lemon juice, and simple syrup to a shaker with ice. Shake vigorously until well chilled. Strain into the prepared shot glass."}'::jsonb, 'Shot glass', '{"en":"Sugar rim and lemon wheel"}'::jsonb, 'none', 'shot', '{"ING_VODKA"}', '{"sweet","sour","citrus"}', 25, 110, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","celebration","brunch"}', false, false, '{"lemon-drop-martini","raspberry-lemon-drop","blueberry-lemon-drop"}', 'Use fresh lemon juice only. Ensure sugar rim is even and not too thick. Can substitute vodka with citrus vodka for extra lemon flavor. Often served with a lemon wedge on the side.', 'low', 90, 'https://en.wikipedia.org/wiki/Lemon_drop_(cocktail)', 'Classic sweet-sour shooter inspired by lemon drop candy.', 1)
