-- 172. Long Beach Iced Tea
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e7f8a9b0-1234-5678-e123-567890123456', 'long-beach-iced-tea', 'c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3', '{"en":"Long Beach Iced Tea"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","strong","party","cranberry","multi-spirit"}', '{"en":"A vibrant variation of the Long Island Iced Tea, swapping cola for cranberry juice. This powerful cocktail combines five spirits with sweet and sour mix and cranberry juice, creating a fruity, deceptively strong drink."}'::jsonb, '{"created_year":"1980","origin":{"city":"Long Beach","state":"California","country":"USA"},"story":{"en":"The Long Beach Iced Tea emerged in the 1980s in Long Beach, California, as a West Coast answer to the Long Island Iced Tea. The substitution of cranberry juice for cola gives the drink a distinctive pink color and fruiter profile. While the Long Island version gained fame in New York, the Long Beach version became popular in California beach bars and clubs. The cranberry juice makes it slightly less sweet than its East Coast cousin while maintaining the same potent multi-spirit formula."},"named_after":{"en":"Named after Long Beach, California, where this variation was created as a West Coast alternative to the Long Island Iced Tea."}}'::jsonb, '{"profile":["fruity","tart","strong","cranberry"],"description":{"en":"Fruity and tart with cranberry taking the lead, masking the substantial alcohol content from five different spirits. The sweet and sour mix adds citrus balance while the cranberry provides a refreshing tartness. Dangerously smooth and deceptively strong."},"first_impression":{"en":"Sweet-tart cranberry with fruity complexity"},"finish":{"en":"Long, warming finish with cranberry tartness and multi-spirit complexity"},"balance":{"en":"The cranberry and sweet-sour balance each other while masking the substantial alcohol - drink with caution"}}'::jsonb, '{"best_time":["evening","night","late_night"],"occasions":["party","celebration","nightclub","beach"],"seasons":["all-year"],"food_pairings":{"en":"Best consumed as a party drink rather than with food. If pairing, works with bar snacks, pizza, and casual party foods."},"ideal_for":{"en":"Perfect for party atmospheres and those who want a strong drink that doesn''t taste overly alcoholic. Great for nightclubs and celebrations. Warning: very high alcohol content - pace yourself."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":15,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":15,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_GIN","quantity":{"amount":15,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_TEQUILA_SILVER","quantity":{"amount":15,"unit":"ml"},"display_name":"Tequila (silver)","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":15,"unit":"ml"},"display_name":"Triple sec","notes":null},{"ingredient_id":"ING_SWEET_AND_SOUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet and sour mix","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Cranberry juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all spirits, sweet and sour mix, and cranberry juice to a cocktail shaker with ice. Shake vigorously. Strain into a tall glass filled with fresh ice. Garnish with a lemon wedge."}'::jsonb, 'Collins glass or Hurricane glass', '{"en":"Lemon wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA","ING_WHITE_RUM","ING_GIN","ING_TEQUILA_SILVER","ING_TRIPLE_SEC"}', '{"fruity","tart","strong","cranberry"}', 22, 245, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"party","celebration","nightclub","beach"}', false, false, '{"long-island-iced-tea","tokyo-tea","miami-iced-tea"}', 'VERY STRONG - contains 5 spirits. Some versions use lemon juice + simple syrup instead of sweet and sour mix. Warn customers about alcohol content. Popular order at beach bars.', 'mid', 75, 'https://www.diffordsguide.com/cocktails/recipe/1250/long-beach-iced-tea', 'West Coast variation of Long Island Iced Tea, emerged 1980s in Long Beach, CA.', 1)
