-- 174. Lynchburg Lemonade
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d6e7f8a9-0123-4567-d012-456789012345', 'lynchburg-lemonade', 'b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2', '{"en":"Lynchburg Lemonade"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","whiskey","jack-daniels","lemonade","refreshing"}', '{"en":"A refreshing and dangerously drinkable cocktail combining Jack Daniel''s Tennessee Whiskey, triple sec, lemon juice, and lemon-lime soda. The Lynchburg Lemonade is sweet, citrusy, and perfect for warm weather drinking."}'::jsonb, '{"created_year":"1980","origin":{"city":"Huntsville","state":"Alabama","country":"USA"},"creator":{"name":"Tony Mason","profession":"bartender"},"story":{"en":"The Lynchburg Lemonade was created in 1980 by Tony Mason, a bartender at TGI Friday''s in Huntsville, Alabama. The drink is named after Lynchburg, Tennessee, the home of Jack Daniel''s Distillery. Despite being created in Alabama, the name honors the famous whiskey''s hometown. The cocktail became extremely popular in the 1980s and 1990s, helped by promotion from Jack Daniel''s. Ironically, Lynchburg is located in a dry county where alcohol sales are prohibited, though the distillery operates under a special permit."},"named_after":{"en":"Named after Lynchburg, Tennessee, the hometown of Jack Daniel''s Distillery."}}'::jsonb, '{"profile":["sweet","citrus","lemon","refreshing"],"description":{"en":"Sweet and citrusy with prominent lemon flavor balanced by Jack Daniel''s smooth whiskey character. The triple sec adds orange notes while the lemon-lime soda provides effervescence. Very easy-drinking and dangerously smooth - the alcohol is well-masked by the sweet citrus."},"first_impression":{"en":"Sweet lemonade with whiskey warmth and citrus brightness"},"finish":{"en":"Sweet, smooth finish with lingering lemon and whiskey notes"},"balance":{"en":"Leans sweet but the citrus and whiskey keep it from being cloying - very well-balanced for a sweet drink"}}'::jsonb, '{"best_time":["afternoon","evening","summer"],"occasions":["casual","party","barbecue","poolside"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Southern barbecue, fried chicken, pulled pork sandwiches, and American comfort food. Great with spicy foods as the sweetness balances heat."},"ideal_for":{"en":"Perfect for Jack Daniel''s fans who want a refreshing, easy-drinking cocktail. Great for summer parties, barbecues, or anyone who enjoys sweet, citrusy drinks. Popular with those new to whiskey."}}'::jsonb, '[{"ingredient_id":"ING_JACK_DANIELS","quantity":{"amount":30,"unit":"ml"},"display_name":"Jack Daniel''s Tennessee Whiskey","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":15,"unit":"ml"},"display_name":"Triple sec","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_LEMON_LIME_SODA","quantity":{"amount":90,"unit":"ml"},"display_name":"Lemon-lime soda (7-Up or Sprite)","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour Jack Daniel''s, triple sec, and fresh lemon juice over ice. Top with lemon-lime soda. Stir gently. Garnish with a lemon wheel and cherry."}'::jsonb, 'Highball glass', '{"en":"Lemon wheel and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_JACK_DANIELS"}', '{"sweet","citrus","lemon","refreshing"}', 9, 205, 'easy', 35, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","party","barbecue","poolside"}', false, false, '{"tennessee-lemonade","whiskey-lemonade"}', 'Traditionally made with Jack Daniel''s specifically. Use fresh lemon juice, not bottled. Can increase whiskey portion for stronger version. Very popular in Southern U.S.', 'low', 70, 'https://www.diffordsguide.com/cocktails/recipe/1249/lynchburg-lemonade', 'Created 1980 by Tony Mason at TGI Friday''s, Huntsville, Alabama.', 1)
