-- 176. Mai Tai (Famous Variant)
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e5f6a7b8-c9d0-1e2f-3a4b-5c6d7e8f9a0b', 'mai-tai-famous', 'mai-tai-famous-variant-tiki-tropical-2025', '{"en":"Mai Tai (Famous Variant)"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","classic-variation","popular"}', '{"en":"The popular tourist-style Mai Tai variant featuring pineapple juice and grenadine, different from the IBA official version. This sweeter, more approachable version became the standard at beach resorts and tiki bars worldwide, making it one of the most recognizable tropical cocktails."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Various","bar":"Beach Resorts","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"While Victor \"Trader Vic\" Bergeron created the original Mai Tai in 1944, this famous variant emerged in the 1960s-1970s as beach resorts and commercial tiki bars adapted the recipe to be more approachable and cost-effective. The addition of pineapple juice and grenadine created a sweeter, more visually appealing drink that resonated with tourists and casual drinkers. Though purists debate its authenticity, this version became so widespread that many people consider it the \"real\" Mai Tai. It''s the version most commonly served at beach resorts, cruise ships, and casual tiki establishments worldwide."},"named_after":{"en":"Retains the name \"Mai Tai\" from the original, meaning \"the best\" or \"out of this world\" in Tahitian, though this variant differs significantly from Trader Vic''s creation."}}'::jsonb, '{"profile":["fruity","tropical","sweet"],"description":{"en":"Sweet and tropical with prominent pineapple and orange flavors. The grenadine adds berry sweetness and a beautiful gradient color. More approachable and fruit-forward than the original, with rum providing warmth in the background. Easy-drinking and crowd-pleasing."},"first_impression":{"en":"Sweet tropical pineapple and orange burst with colorful presentation"},"finish":{"en":"Medium finish with lingering tropical fruit and gentle rum warmth"},"balance":{"en":"Sweet and fruity with good tropical balance - designed for mass appeal rather than complexity"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","vacation","cruise","casual_gathering"],"seasons":["summer","year_round"],"food_pairings":{"en":"Perfect with Hawaiian pizza, coconut shrimp, fish tacos, tropical fruit platters, or casual beach food. Also pairs well with grilled chicken and summer BBQ fare."},"ideal_for":{"en":"Perfect for beach vacations, pool parties, and casual tropical gatherings. Ideal for those who prefer sweeter, more approachable cocktails. The go-to Mai Tai at most beach resorts and cruise ships."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Orange juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Grenadine","notes":null}]'::jsonb, 'shake', '{"en":"Add white rum, dark rum, pineapple juice, orange juice, and lime juice to a shaker with ice. Shake well. Fill a hurricane glass with crushed ice. Strain mixture into glass. Drizzle grenadine down the side to create a sunset gradient effect. Garnish with pineapple wedge, cherry, and mint."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge, maraschino cherry, and fresh mint sprig"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_WHITE","ING_RUM_DARK"}', '{"fruity","tropical","sweet"}', 12, 260, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","year_round"}', '{"beach","pool_party","vacation","cruise"}', false, false, '{"mai-tai-iba-official","frozen-mai-tai","virgin-mai-tai"}', 'This is the popular tourist/resort version, NOT the IBA official recipe. Most customers expect this version when ordering Mai Tai at beach bars. Grenadine sunset effect is visual signature. Sweeter and more approachable than authentic Trader Vic recipe. Very popular poolside drink.', 'mid', 90, 'https://www.liquor.com/recipes/mai-tai/', 'Popular beach resort variant. Differs from IBA official and original Trader Vic recipes.', 1)
