-- 178. Mexican Firing Squad
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c3d4e5f6-a7b8-4c9d-0e1f-2a3b4c5d6e7f', 'mexican-firing-squad', 'd0e9f8a7b6c59483928170695847362fdbecad03', '{"en":"Mexican Firing Squad"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","tequila","citrus","spicy"}', '{"en":"A bold tequila cocktail featuring lime juice, grenadine, and Angostura bitters. The Mexican Firing Squad is a spicy, citrus-forward drink with a beautiful pink hue and complex flavor profile."}'::jsonb, '{"created_year":"1937","origin":{"city":"Mexico","bar":"Unknown","country":"Mexico"},"creator":{"name":"Charles H. Baker Jr.","profession":"writer"},"story":{"en":"First documented by Charles H. Baker Jr. in his 1937 book \"The Gentleman''s Companion,\" the Mexican Firing Squad is a forgotten classic that was rediscovered during the modern cocktail renaissance. Baker encountered the drink during his travels in Mexico. The cocktail fell into obscurity until it was revived by craft bartenders in the 2000s who appreciated its bold flavors and unusual combination of tequila with grenadine and bitters."},"named_after":{"en":"The dramatic name references the Mexican tradition of offering a final drink to condemned prisoners before execution."}}'::jsonb, '{"profile":["citrus","spicy","complex"],"description":{"en":"Bright and citrus-forward with unexpected complexity. The tequila provides an agave backbone, lime brings sharp acidity, grenadine adds fruity sweetness, and Angostura bitters contribute warm spice notes that tie everything together."},"first_impression":{"en":"Sharp lime and tequila hit first, followed by fruity grenadine and warm bitters"},"finish":{"en":"Long finish with lingering spice and citrus notes"},"balance":{"en":"Well-balanced between tart, sweet, and spicy elements, creating a complex yet approachable cocktail"}}'::jsonb, '{"best_time":["evening","late_afternoon"],"occasions":["cocktail_hour","dinner_party","celebration"],"seasons":["all_seasons"],"food_pairings":{"en":"Excellent with Mexican cuisine, tacos, ceviche, grilled meats, and spicy dishes. The citrus and spice complement bold, flavorful foods."},"ideal_for":{"en":"Perfect for tequila lovers who enjoy bold, complex cocktails. Ideal for those seeking something different from typical margaritas."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_BLANCO","quantity":{"amount":60,"unit":"ml"},"display_name":"Tequila blanco","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Grenadine","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":6,"unit":"dashes"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a rocks glass filled with fresh ice. Garnish with a lime wheel."}'::jsonb, 'Rocks glass', '{"en":"Lime wheel"}'::jsonb, 'cubes', 'on_rocks', '{"ING_TEQUILA_BLANCO"}', '{"citrus","spicy","complex"}', 16, 155, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"all-seasons"}', '{"cocktail_hour","dinner_party","celebration"}', false, false, '{"margarita","paloma"}', 'Use quality grenadine (preferably house-made) for best results. The Angostura bitters are essential - they provide the signature spicy character. Can garnish with a chili salt rim for extra heat.', 'mid', 68, 'The Gentleman''s Companion by Charles H. Baker Jr., 1937', 'Rediscovered classic from 1937. Revived during modern cocktail renaissance.', 1)
