-- 180. Mezcal Negroni
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b8c9d0e1-f2a3-4b4c-5d6e-7f8a9b0c1d2e', 'mezcal-negroni', '827364958473920183746574839201837465748393', '{"en":"Mezcal Negroni"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","mezcal","bitter","smoky"}', '{"en":"A bold, smoky twist on the classic Negroni, replacing gin with mezcal. The earthy, smoky character of mezcal adds a new dimension to the bitter-sweet interplay of Campari and sweet vermouth, creating a more complex and contemplative drinking experience."}'::jsonb, '{"created_year":"2000s","origin":{"city":"New York City","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Mezcal Negroni emerged in the mid-2000s as part of the craft cocktail renaissance, when bartenders began experimenting with mezcal in classic cocktail formulas. The Negroni, with its simple equal-parts structure, was an ideal canvas for showcasing mezcal''s distinctive character. The smoky agave spirit proved to be a natural match for Campari''s bitter complexity and sweet vermouth''s herbal notes. By the 2010s, it had become a staple on cocktail menus worldwide, introducing many drinkers to mezcal through the familiar Negroni format."},"named_after":{"en":"Named after mezcal, the smoky agave spirit that replaces gin in the classic Negroni formula."}}'::jsonb, '{"profile":["bitter","smoky","herbal","complex"],"description":{"en":"Bold, complex, and intensely flavorful. Mezcal''s smoke mingles with Campari''s bitter orange and herbal notes, while sweet vermouth adds depth and balances the bitterness. More earthy and rustic than a gin Negroni, with a longer, more contemplative finish. The smoke doesn''t dominate but adds a sophisticated layer."},"first_impression":{"en":"Bitter Campari and smoke hit first, followed by herbal complexity"},"finish":{"en":"Long, smoky finish with lingering bitter and herbal notes"},"balance":{"en":"Well-balanced - smoke adds depth without overwhelming bitter-sweet interplay"}}'::jsonb, '{"best_time":["evening","aperitivo","late_night"],"occasions":["aperitivo","date_night","contemplative_drinking","digestivo"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Excellent as an aperitivo with cured meats, aged cheeses, or olives. Also pairs well with grilled meats, Mexican mole, smoked dishes, and rich, savory foods."},"ideal_for":{"en":"Perfect for Negroni lovers seeking more complexity. Ideal for mezcal enthusiasts and adventurous drinkers who appreciate bitter cocktails. Great for those who want a contemplative, sipping experience."}}'::jsonb, '[{"ingredient_id":"ING_MEZCAL","quantity":{"amount":30,"unit":"ml"},"display_name":"Mezcal","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":30,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_SWEET_VERMOUTH","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet vermouth","notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir gently for 30-40 seconds until well chilled and diluted. Strain into a rocks glass over a large ice cube. Garnish with an orange twist, expressing oils over the drink."}'::jsonb, 'Rocks glass', '{"en":"Orange twist"}'::jsonb, 'large_cube', 'on_the_rocks', '{"ING_MEZCAL"}', '{"bitter","smoky","herbal","complex"}', 24, 190, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"aperitivo","date_night","contemplative_drinking","digestivo"}', false, false, '{"oaxaca-old-fashioned","mezcal-boulevardier","naked-and-famous"}', 'Use quality artisanal mezcal. Stir, don''t shake. Large ice cube preferred to minimize dilution. Express orange oils over drink before garnishing. Can do half mezcal, half gin for lighter smoke.', 'mid', 75, 'https://www.diffordsguide.com/cocktails/recipe/5345/mezcal-negroni', 'Modern variation popularized during 2000s-2010s craft cocktail renaissance.', 1)
