-- 181. Mudslide
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-f6a7-4b8c-9d0e-1f2a3b4c5d6e', 'mudslide', 'mudslide_vodka_kahlua_baileys_cream', '{"en":"Mudslide"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","coffee","chocolate","vodka-based"}', '{"en":"A decadent frozen cocktail blending vodka, coffee liqueur, Irish cream, and heavy cream. The Mudslide is essentially a boozy milkshake that combines coffee and chocolate flavors into a rich, indulgent dessert drink."}'::jsonb, '{"created_year":"1970s","origin":{"city":"Grand Cayman","bar":"Wreck Bar","country":"Cayman Islands"},"story":{"en":"The Mudslide was created in the 1970s at the Wreck Bar on Grand Cayman Island. The original version was made with vodka, Kahlúa, and Baileys Irish Cream, served over ice. As the drink gained popularity in beach bars and resorts, the frozen blended version became more common, transforming it into the dessert-like cocktail we know today. The name references the drink''s thick, muddy appearance when blended."},"named_after":{"en":"Named after its thick, muddy brown appearance when blended, resembling an actual mudslide."}}'::jsonb, '{"profile":["creamy","sweet","coffee","chocolate"],"description":{"en":"Extremely rich and dessert-like, with chocolate and coffee flavors melding into a creamy, ice cream-like consistency. Sweet and indulgent with minimal alcohol taste despite the high spirit content."},"first_impression":{"en":"Cold, creamy sweetness with immediate chocolate and coffee notes"},"finish":{"en":"Long, sweet finish with lingering chocolate and Irish cream flavors"},"balance":{"en":"Heavily weighted toward sweetness and cream - this is a dessert drink first and foremost"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["dessert","celebration","beach","casual"],"seasons":["summer","all_seasons"],"food_pairings":{"en":"Best enjoyed as a dessert replacement. Pairs well with light appetizers or as an after-dinner treat. Can accompany chocolate cake or brownie."},"ideal_for":{"en":"Perfect for those who love frozen dessert drinks and boozy milkshakes. Ideal for beach vacations, poolside relaxation, or anyone with a serious sweet tooth. Popular in tropical resort settings."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":30,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'blend', '{"en":"Add all ingredients to a blender with 1 cup of ice. Blend until smooth and creamy. Optionally, drizzle chocolate syrup inside a hurricane or poco grande glass. Pour blended mixture into glass. Top with whipped cream and chocolate shavings."}'::jsonb, 'Hurricane (or Poco Grande)', '{"en":"Whipped cream, chocolate shavings, chocolate syrup drizzle"}'::jsonb, 'blended', 'frozen', '{"ING_VODKA"}', '{"creamy","sweet","coffee","chocolate"}', 12, 420, 'easy', 90, '{"milk"}', '{"lactose","alcohol","caffeine"}', '{"vegetarian","pescatarian","gluten_free"}', 0, '{"vegetarian","gluten-free"}', '{"summer","all-seasons"}', '{"dessert","casual","celebration"}', false, false, '{"chocolate-mudslide","peanut-butter-mudslide","frozen-mudslide"}', 'Can be served on the rocks or frozen - frozen version is more popular. Use vanilla ice cream instead of cream for an even richer version. Chocolate rim optional but popular. High calorie count - warn health-conscious guests.', 'mid', 75, 'https://www.liquor.com/recipes/mudslide/', 'Classic frozen dessert cocktail from Cayman Islands.', 1)
