-- 182. Navy Grog
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('5e6f7a8b-9c0d-1e2f-3a4b-5c6d7e8f9a0b', 'navy-grog', 'navy_grog_famous_tiki_tropical_cocktail', '{"en":"Navy Grog"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","citrus","strong"}', '{"en":"The Navy Grog is a classic tiki cocktail created by Don the Beachcomber, featuring three types of rum with honey, citrus, and club soda. Traditionally served with an ice cone, this refreshing yet potent drink honors naval grog traditions with a tiki twist."}'::jsonb, '{"created_year":"1941","origin":{"city":"Hollywood","bar":"Don the Beachcomber","country":"USA"},"creator":{"name":"Ernest Raymond Beaumont Gantt (Donn Beach)","profession":"bartender"},"story":{"en":"The Navy Grog was created in 1941 by Ernest Raymond Beaumont Gantt, better known as Don the Beachcomber, at his Hollywood restaurant. The drink pays homage to traditional naval grog - the rum ration given to British sailors since the 18th century. Don''s version elevated the simple rum-and-water mixture into a sophisticated tiki cocktail by combining three different rums with honey, fresh citrus, and club soda. The signature presentation features a carved ice cone in the center of the glass, which slowly dilutes the drink as it melts. During World War II, the Navy Grog became particularly popular with servicemen on leave. The recipe remained a closely guarded secret for decades."},"named_after":{"en":"Named after traditional naval grog, the daily rum ration given to British Royal Navy sailors from 1740 to 1970."}}'::jsonb, '{"profile":["citrus","refreshing","strong"],"description":{"en":"Bright and refreshing with complex rum depth and honeyed citrus character. The Navy Grog balances three different rums with tart grapefruit and lime, sweetened with honey and lengthened with soda. The ice cone provides controlled dilution, making it refreshing yet substantial."},"first_impression":{"en":"Bright citrus and honey hit first, followed by the complex warmth of triple rum blend"},"finish":{"en":"Long, refreshing finish with lingering citrus and subtle rum complexity, smoothed by honey"},"balance":{"en":"Well-balanced between strong rum foundation and refreshing citrus-soda lift, honey provides smooth integration"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["tiki_bar","summer_gathering","outdoor_party"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Caribbean jerk dishes, grilled seafood, fish tacos, or Polynesian appetizers. Also pairs well with spicy Asian cuisine and tropical fruit salads."},"ideal_for":{"en":"Perfect for tiki purists and rum enthusiasts seeking an authentic Don the Beachcomber experience. Ideal for those who appreciate balanced, citrus-forward cocktails with serious rum complexity. Great for hot weather refreshment."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum (Jamaican)","notes":null},{"ingredient_id":"ING_RUM_DEMERARA","quantity":{"amount":30,"unit":"ml"},"display_name":"Demerara rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Honey syrup","notes":null},{"ingredient_id":"ING_CLUB_SODA","quantity":{"amount":30,"unit":"ml"},"display_name":"Club soda","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients except club soda to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Pour unstrained into a double old-fashioned glass. Add club soda. Traditionally, place an ice cone in the center of the drink. Garnish with mint sprig."}'::jsonb, 'Double old-fashioned', '{"en":"Fresh mint sprig, ice cone (traditional)"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_WHITE","ING_RUM_DARK","ING_RUM_DEMERARA"}', '{"citrus","refreshing","strong"}', 16, 240, 'intermediate', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"tiki_bar","summer_gathering","outdoor_party"}', false, false, '{"don-the-beachcomber-navy-grog"}', 'Original Don the Beachcomber recipe from 1941. Three-rum blend is essential - each rum adds unique character. Ice cone is traditional but optional - if making cone, carve from block ice. Honey syrup is 1:1 honey to water. Pour unstrained for authentic presentation.', 'premium', 70, 'https://beachbumberry.com/navy-grog.html', 'Original Don the Beachcomber recipe from 1941. Jeff "Beachbum" Berry''s Sippin'' Safari. Tiki cocktail archives.', 1)
