-- 184. Oaxaca Old Fashioned
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c9d0e1f2-a3b4-4c5d-6e7f-8a9b0c1d2e3f', 'oaxaca-old-fashioned', '726354849302847563829104857362918475638291', '{"en":"Oaxaca Old Fashioned"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","mezcal","tequila","smoky"}', '{"en":"A revolutionary modern classic that reimagines the Old Fashioned with agave spirits. Created by Phil Ward, this cocktail combines reposado tequila and mezcal with agave nectar and bitters, creating a smoky, complex drink that respects the Old Fashioned format while being entirely its own."}'::jsonb, '{"created_year":"2007","origin":{"city":"New York City","bar":"Death & Co","country":"USA"},"creator":{"name":"Phil Ward","profession":"bartender"},"story":{"en":"Phil Ward created the Oaxaca Old Fashioned in 2007 at Death & Co, one of New York''s pioneering craft cocktail bars. Ward wanted to showcase mezcal but knew many guests were intimidated by its intense smoky flavor. By splitting the base between reposado tequila and mezcal, and using agave nectar instead of simple syrup, he created a more approachable introduction to mezcal while maintaining complexity. The drink became an instant sensation and is considered one of the most influential modern cocktails, helping launch the mezcal boom of the 2010s."},"named_after":{"en":"Named after Oaxaca, Mexico, the heartland of mezcal production and the source of the cocktail''s smoky soul."}}'::jsonb, '{"profile":["smoky","agave","complex","stirred"],"description":{"en":"Beautifully balanced and complex. Reposado tequila provides a smooth agave base with vanilla and oak notes, while mezcal adds smoky depth without overwhelming. Agave nectar enhances the agave character, and mole bitters add chocolate-spice complexity. Richer and earthier than a whiskey Old Fashioned, with a silky texture and long finish."},"first_impression":{"en":"Smooth agave and subtle smoke hit first, followed by chocolate-spice notes"},"finish":{"en":"Long, warming finish with lingering smoke, agave, and mole spice"},"balance":{"en":"Expertly balanced - tequila smooths mezcal''s intensity, creating approachable complexity"}}'::jsonb, '{"best_time":["evening","late_night","contemplative"],"occasions":["date_night","contemplative_drinking","digestivo","special_occasion"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Excellent with Mexican cuisine, especially mole dishes, grilled meats, or dark chocolate desserts. Also pairs well with aged cheeses, smoked foods, and rich, savory dishes."},"ideal_for":{"en":"Perfect for Old Fashioned lovers seeking new experiences. Ideal for those exploring agave spirits or wanting an introduction to mezcal. Great for contemplative sipping and conversation."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_REPOSADO","quantity":{"amount":45,"unit":"ml"},"display_name":"Reposado tequila","notes":null},{"ingredient_id":"ING_MEZCAL","quantity":{"amount":15,"unit":"ml"},"display_name":"Mezcal","notes":null},{"ingredient_id":"ING_AGAVE_SYRUP","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Agave syrup","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dashes"},"display_name":"Angostura bitters","notes":null},{"ingredient_id":"ING_MOLE_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Mole bitters","optional":true,"notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir gently for 30-40 seconds until properly chilled and diluted. Strain into a rocks glass over a large ice cube. Garnish with a flamed orange peel, expressing oils over the drink."}'::jsonb, 'Rocks glass', '{"en":"Flamed orange peel"}'::jsonb, 'large_cube', 'on_the_rocks', '{"ING_TEQUILA_REPOSADO","ING_MEZCAL"}', '{"smoky","agave","complex","stirred"}', 32, 180, 'intermediate', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"date_night","contemplative_drinking","digestivo","special_occasion"}', false, false, '{"mezcal-old-fashioned","tequila-old-fashioned","sotol-old-fashioned"}', 'Mole bitters essential for authentic version - adds chocolate-spice complexity. Flame orange peel for theatrical presentation and caramelization. Use quality reposado and artisanal mezcal. Large ice cube minimizes dilution.', 'premium', 85, 'https://www.deathandcompany.com/', 'Created by Phil Ward at Death & Co, New York City, 2007. One of the most influential modern cocktails.', 1)
