-- 186. Painkiller
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('8f7a2c3d-4e5b-6c7d-8e9f-0a1b2c3d4e5f', 'painkiller', 'painkiller_famous_tiki_tropical_cocktail', '{"en":"Painkiller"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","creamy","sweet"}', '{"en":"The Painkiller is the signature cocktail of the British Virgin Islands, combining dark rum with pineapple juice, orange juice, and coconut cream. This creamy tropical drink topped with nutmeg is the ultimate island escape in a glass."}'::jsonb, '{"created_year":"1970","origin":{"city":"White Bay","bar":"Soggy Dollar Bar","country":"British Virgin Islands"},"creator":{"name":"Daphne Henderson","profession":"bar owner"},"story":{"en":"The Painkiller was created in the 1970s by Daphne Henderson at the Soggy Dollar Bar on Jost Van Dyke in the British Virgin Islands. The bar got its name because customers had to swim to shore to reach it, arriving with soggy dollar bills. In 1989, Pusser''s Rum trademarked the name \"Painkiller\" and established the official recipe. The drink became so popular that it spawned numerous variations and inspired tropical cocktail culture worldwide."},"named_after":{"en":"Named for its reputation as a hangover cure and the perfect remedy for any ailment while on vacation in paradise."}}'::jsonb, '{"profile":["tropical","creamy","sweet"],"description":{"en":"Rich and indulgent with layers of tropical fruit flavors. The Painkiller balances pineapple brightness with orange tang, coconut creaminess, and the deep, warming notes of aged rum, finished with aromatic nutmeg."},"first_impression":{"en":"Sweet tropical fruits and coconut cream hit immediately, with rum warmth building underneath"},"finish":{"en":"Long, creamy finish with lingering nutmeg spice and rum warmth"},"balance":{"en":"Perfectly balanced between sweet tropical juices and rich coconut cream, with enough rum to keep it interesting"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","vacation","summer_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Caribbean jerk chicken, fish tacos, coconut shrimp, or tropical fruit platters. Also excellent with spicy foods as the cream helps balance the heat."},"ideal_for":{"en":"Perfect for beach lovers and tropical cocktail enthusiasts. Ideal for anyone seeking a vacation vibe or looking to escape to an island paradise. Great for those who enjoy creamy, dessert-like cocktails."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":60,"unit":"ml"},"display_name":"Dark rum (Pusser''s)","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Orange juice","notes":null},{"ingredient_id":"ING_COCONUT_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Cream of coconut","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a hurricane glass filled with crushed ice. Garnish generously with freshly grated nutmeg on top."}'::jsonb, 'Hurricane glass', '{"en":"Freshly grated nutmeg, pineapple wedge, orange slice"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK"}', '{"tropical","creamy","sweet"}', 12, 320, 'easy', 90, '{"coconut","sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","pool_party","vacation","summer_gathering"}', false, false, '{"painkiller-2","painkiller-3","painkiller-4"}', 'Original recipe calls for Pusser''s Navy Rum. Ratios vary: Painkiller #2 (2oz rum), #3 (3oz rum), #4 (4oz rum). Always use freshly grated nutmeg, not pre-ground. Crushed ice is essential for proper dilution.', 'mid', 85, 'https://pussersrum.com/painkiller-cocktail/', 'Official Pusser''s Rum recipe. Historical information from Soggy Dollar Bar and Caribbean cocktail sources.', 1)
