-- 191. Piña Colada (Famous Variant)
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f6a7b8c9-d0e1-2f3a-4b5c-6d7e8f9a0b1c', 'pina-colada-famous', 'pina-colada-famous-variant-tiki-tropical-2025', '{"en":"Piña Colada (Famous Variant)"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","creamy","frozen","popular"}', '{"en":"The popular frozen variant of the classic Piña Colada, blended to a smooth, slushie-like consistency. This beach resort favorite combines rum, coconut cream, and pineapple juice into an indulgent frozen treat that epitomizes tropical vacation vibes."}'::jsonb, '{"created_year":"1970s","origin":{"city":"Various","bar":"Beach Resorts","country":"Puerto Rico / USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"While the original Piña Colada was created in Puerto Rico in the 1950s (claimed by both Ramón \"Monchito\" Marrero at Caribe Hilton and Ricardo García at Barrachina), the frozen blended version became the dominant style in the 1970s with the widespread availability of electric blenders. This frozen variant, immortalized in the 1979 Rupert Holmes song \"Escape (The Piña Colada Song),\" became the quintessential beach resort cocktail. The frozen version is now so ubiquitous that most people think of it as the \"original\" way to serve a Piña Colada. It became the official drink of Puerto Rico in 1978."},"named_after":{"en":"Spanish for \"strained pineapple,\" referring to the fresh pineapple juice that is a key ingredient. The name remained the same from the original recipe."}}'::jsonb, '{"profile":["creamy","tropical","sweet"],"description":{"en":"Incredibly creamy and smooth with pronounced coconut and pineapple flavors. The frozen texture makes it like a tropical milkshake - rich, indulgent, and refreshing. Sweet and luscious with the rum providing subtle warmth beneath the cold, creamy surface."},"first_impression":{"en":"Cold, creamy coconut and sweet pineapple with smooth frozen texture"},"finish":{"en":"Long, creamy finish with lingering coconut sweetness and gentle rum warmth"},"balance":{"en":"Sweet and creamy with good coconut-pineapple balance - the frozen format makes it more like a dessert than a cocktail"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","vacation","cruise","tropical_celebration"],"seasons":["summer","year_round"],"food_pairings":{"en":"Perfect as a dessert replacement or alongside tropical fruit platters, coconut shrimp, fish tacos, or light Caribbean fare. Also pairs well with grilled pineapple and coconut cake."},"ideal_for":{"en":"Perfect for beach vacations, pool parties, and cruise ships. Ideal for those who love creamy, dessert-like cocktails. The ultimate tropical indulgence and beach resort staple. Great for hot summer days."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":60,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_CREAM_OF_COCONUT","quantity":{"amount":45,"unit":"ml"},"display_name":"Cream of coconut","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Fresh pineapple juice","notes":null}]'::jsonb, 'blend', '{"en":"Add rum, cream of coconut, pineapple juice, and 1.5 cups of ice to a blender. Blend on high speed until smooth and creamy, with a consistency like a frozen milkshake. Pour into a hurricane glass or tiki mug. Garnish with pineapple wedge and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'blended', 'frozen', '{"ING_RUM_WHITE"}', '{"creamy","tropical","sweet"}', 10, 320, 'easy', 90, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegetarian","gluten-free"}', '{"summer","year_round"}', '{"beach","pool_party","vacation","cruise"}', false, false, '{"pina-colada-iba-official","virgin-pina-colada","strawberry-colada","lava-flow"}', 'This is the popular frozen version, different from IBA official recipe. Blend until smooth like a milkshake. Use Coco López or similar cream of coconut (NOT coconut milk). Fresh pineapple juice highly recommended. Official drink of Puerto Rico since 1978. Made famous by Rupert Holmes song. Very popular at beach resorts.', 'mid', 95, 'https://www.liquor.com/recipes/pina-colada/', 'Popular frozen variant. Official drink of Puerto Rico. Different from IBA official recipe.', 1)
