-- 194. Presbyterian
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d0e1f2a3-4567-8901-d456-890123456789', 'presbyterian', 'b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6', '{"en":"Presbyterian"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","whiskey","bourbon","refreshing","classic"}', '{"en":"A classic American highball combining whiskey with ginger ale and club soda. The Presbyterian is a light, refreshing drink that softens the whiskey''s edge with effervescence, making it perfect for casual sipping."}'::jsonb, '{"created_year":"1895","origin":{"city":"United States","country":"USA"},"story":{"en":"The Presbyterian dates back to the late 19th century and is one of America''s oldest highball cocktails. The name''s origin is debated - some believe it references the Presbyterian church''s temperance movement (the drink being relatively light on alcohol), while others suggest it was simply a popular drink among Presbyterian communities. The combination of ginger ale and club soda creates a unique effervescence that sets it apart from simpler whiskey highballs. It gained popularity in country clubs and became a staple \"golf course drink\" in the mid-20th century."},"named_after":{"en":"Likely named after the Presbyterian church community, though the exact origin remains uncertain."}}'::jsonb, '{"profile":["refreshing","spicy","ginger","whiskey"],"description":{"en":"Light and refreshing with whiskey warmth balanced by ginger spice and carbonation. The dual soda combination creates a unique texture that''s more effervescent than a standard highball, while the ginger ale adds subtle sweetness and spice."},"first_impression":{"en":"Effervescent and spicy with ginger and whiskey warmth"},"finish":{"en":"Clean, refreshing finish with lingering ginger spice"},"balance":{"en":"Well-balanced between whiskey character and refreshing carbonation - neither too strong nor too diluted"}}'::jsonb, '{"best_time":["afternoon","evening","daytime"],"occasions":["casual","golf","outdoor","social"],"seasons":["all-year"],"food_pairings":{"en":"Pairs well with grilled meats, barbecue, burgers, and pub fare. Excellent with smoked foods and charcuterie. Traditional golf course accompaniment."},"ideal_for":{"en":"Perfect for whiskey lovers who want a lighter, more refreshing drink. Great for hot days, golf outings, or casual social gatherings. Ideal for those who find straight whiskey too strong but enjoy its flavor."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":60,"unit":"ml"},"display_name":"Bourbon or rye whiskey","notes":null},{"ingredient_id":"ING_GINGER_ALE","quantity":{"amount":60,"unit":"ml"},"display_name":"Ginger ale","notes":null},{"ingredient_id":"ING_CLUB_SODA","quantity":{"amount":60,"unit":"ml"},"display_name":"Club soda","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour whiskey over ice. Add equal parts ginger ale and club soda. Stir gently to combine. Garnish with a lemon twist or lime wedge."}'::jsonb, 'Highball glass', '{"en":"Lemon twist or lime wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_BOURBON"}', '{"refreshing","spicy","ginger","whiskey"}', 10, 145, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"casual","golf","outdoor"}', false, false, '{"whiskey-ginger","mamie-taylor"}', 'Equal parts ginger ale and club soda is traditional, but ratio can be adjusted to taste. Works with bourbon, rye, or blended whiskey. Sometimes called "Press" in some regions.', 'low', 65, 'https://www.diffordsguide.com/cocktails/recipe/1243/presbyterian', 'Classic American highball from late 19th century.', 1)
