-- 198. Rum and Coke
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a3b4c5d6-7890-1234-a789-123456789012', 'rum-and-coke', 'e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9', '{"en":"Rum and Coke"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","rum","cola","simple","classic","cuba"}', '{"en":"One of the world''s most popular cocktails, combining rum with cola and lime. Simple yet timeless, the Rum and Coke (or Cuba Libre with lime) is the ultimate crowd-pleaser and a staple in bars worldwide."}'::jsonb, '{"created_year":"1900","origin":{"city":"Havana","country":"Cuba"},"story":{"en":"The Rum and Coke emerged around 1900 in Havana, Cuba, shortly after Coca-Cola arrived in the Caribbean. The drink gained its patriotic name \"Cuba Libre\" (Free Cuba) during the Spanish-American War when American soldiers mixed local rum with the newly available Coca-Cola and toasted to Cuba''s independence. The addition of lime juice transforms it into the Cuba Libre, though many call any rum and cola combination by this name. It became one of the most ordered drinks worldwide and remains a symbol of Cuban-American cultural exchange."},"named_after":{"en":"The patriotic name \"Cuba Libre\" (Free Cuba) refers to Cuba''s independence movement. Without lime, it''s simply called \"Rum and Coke.\""}}'::jsonb, '{"profile":["sweet","cola","caramel","lime"],"description":{"en":"Sweet cola flavor with rum''s vanilla and caramel notes, brightened by fresh lime. The rum adds warmth and complexity to the cola''s sweetness, while lime provides a crucial citrus balance that elevates it beyond a simple mixer."},"first_impression":{"en":"Sweet cola with rum warmth and bright lime"},"finish":{"en":"Sweet cola finish with lingering rum spice and citrus"},"balance":{"en":"The lime is essential for balance - without it, overly sweet; with it, perfectly refreshing"}}'::jsonb, '{"best_time":["afternoon","evening","night"],"occasions":["casual","party","beach","social"],"seasons":["all-year"],"food_pairings":{"en":"Pairs excellently with Cuban food, burgers, pizza, BBQ, fried foods, and casual pub fare. Perfect with spicy foods as the sweetness balances heat."},"ideal_for":{"en":"Perfect for anyone seeking an easy, universally appealing drink. Great for casual settings, parties, or beach bars. Ideal for those who want something simple that tastes good without complexity."}}'::jsonb, '[{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":50,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_COCA_COLA","quantity":{"amount":120,"unit":"ml"},"display_name":"Coca-Cola","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":10,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour rum over ice. Squeeze fresh lime wedge into glass and drop it in. Top with Coca-Cola. Stir gently once."}'::jsonb, 'Highball glass', '{"en":"Lime wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_WHITE_RUM"}', '{"sweet","cola","caramel","lime"}', 10, 185, 'easy', 25, '{"sulphites"}', '{"alcohol","sulphites_intolerance","caffeine_sensitivity"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"casual","party","beach"}', false, false, '{"cuba-libre","captain-and-coke","bacardi-and-coke"}', 'With lime = Cuba Libre. Without lime = Rum and Coke. Use Mexican Coke (cane sugar) for best flavor if available. Fresh lime is crucial - don''t skip it. Works with white, gold, or dark rum.', 'low', 98, 'https://www.diffordsguide.com/cocktails/recipe/1246/cuba-libre', 'Born in Havana circa 1900, one of world''s most popular cocktails.', 1)
