-- 200. Salty Dog
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-6f78-9012-bcde-f12345678901', 'salty-dog', 'f9a0b1c2d3e4f5a6b7c8d9e0f1a2b3c4d5e6f7a8', '{"en":"Salty Dog"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","refreshing","citrus","vodka","salted-rim"}', '{"en":"A Greyhound with a salted rim. This classic highball combines vodka and fresh grapefruit juice with a generous coating of coarse salt on the rim, creating a perfect balance of sweet, sour, bitter, and salty flavors."}'::jsonb, '{"created_year":"1952","origin":{"city":"United States","country":"USA"},"story":{"en":"The Salty Dog is a variation of the Greyhound that emerged in the early 1950s. The addition of the salt rim was inspired by the Margarita''s salted edge, which had gained popularity around the same time. Originally made with gin, the vodka version became more popular during the vodka boom of the 1960s and 1970s. The name \"Salty Dog\" is a nautical term for a seasoned sailor, fitting the drink''s coastal, refreshing character."},"named_after":{"en":"Named after the nautical slang term for an experienced sailor who has spent considerable time at sea."}}'::jsonb, '{"profile":["citrus","bitter","salty","refreshing"],"description":{"en":"The salt rim transforms the drink, adding a savory dimension that enhances the grapefruit''s natural tartness and bitterness. Each sip provides a complex interplay between the salty edge, bitter citrus, and clean vodka."},"first_impression":{"en":"Salty burst followed by bright, tangy grapefruit"},"finish":{"en":"Clean citrus finish with lingering salt and grapefruit bitterness"},"balance":{"en":"The salt rim creates a perfect harmony between sweet, sour, bitter, and salty - a true four-dimensional cocktail"}}'::jsonb, '{"best_time":["afternoon","brunch","daytime"],"occasions":["casual","brunch","poolside","beach","summer_party"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with oysters, ceviche, fish tacos, grilled shrimp, and other seafood. Also pairs well with chips and guacamole."},"ideal_for":{"en":"Perfect for beach lovers and anyone who enjoys savory cocktails. Great for those who appreciate the salt-citrus combination found in margaritas but want something lighter and less sweet."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_SALT","quantity":{"amount":1,"unit":"tsp"},"display_name":"Coarse salt (for rim)","notes":null}]'::jsonb, 'build', '{"en":"Rim a highball glass with coarse salt by moistening the rim with grapefruit juice and dipping in salt. Fill glass with ice. Pour vodka over ice, then top with fresh grapefruit juice. Stir gently. Garnish with a grapefruit wedge."}'::jsonb, 'Highball glass', '{"en":"Grapefruit wedge, salted rim"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"citrus","bitter","salty","refreshing"}', 8, 155, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","brunch","poolside","beach"}', false, false, '{"greyhound","gin-salty-dog"}', 'Use coarse salt, not fine table salt, for proper rim texture. Pink Himalayan salt looks beautiful. Original recipe used gin instead of vodka - offer as variation. Be generous with the salt rim.', 'low', 75, 'https://www.diffordsguide.com/cocktails/recipe/1235/salty-dog', 'Variant of the Greyhound, documented in classic cocktail literature.', 1)
