-- 202. Saturn
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9c0d1e2f-3a4b-5c6d-7e8f-9a0b1c2d3e4f', 'saturn', 'saturn_famous_tiki_tropical_cocktail', '{"en":"Saturn"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","gin","citrus","unique"}', '{"en":"The Saturn is a unique tiki cocktail that breaks convention by using gin instead of rum, combined with passion fruit, falernum, lemon juice, and orgeat. This space-age drink is tart, nutty, and refreshingly different from traditional tiki fare."}'::jsonb, '{"created_year":"1967","origin":{"city":"California","bar":"Unknown tiki establishment","country":"USA"},"creator":{"name":"J. \"Popo\" Galsini","profession":"bartender"},"story":{"en":"The Saturn was created in 1967 by bartender J. \"Popo\" Galsini for a tiki drink competition. The drink won its category, standing out for its unusual use of gin in a tiki context - at a time when rum absolutely dominated the genre. The name \"Saturn\" reflects the space-age zeitgeist of the 1960s, when humanity was reaching for the stars. The cocktail''s unique combination of gin, passion fruit, falernum, and orgeat created a flavor profile unlike any other tiki drink. Despite winning its competition, the Saturn fell into obscurity for decades until the modern tiki revival brought it back into the spotlight. Today, it''s celebrated as a refreshing alternative to rum-heavy tiki drinks."},"named_after":{"en":"Named after the planet Saturn, reflecting the space-age enthusiasm of the 1960s Space Race era."}}'::jsonb, '{"profile":["citrus","tart","nutty"],"description":{"en":"Bright, tart, and refreshingly complex with gin botanicals playing against tropical passion fruit. The Saturn balances sharp lemon acidity with nutty orgeat and spiced falernum, while gin provides an herbal backbone rarely found in tiki drinks. Unique and memorable."},"first_impression":{"en":"Tart passion fruit and lemon hit first, followed by juniper and herbal gin notes"},"finish":{"en":"Clean, refreshing finish with lingering almond and spice notes from orgeat and falernum"},"balance":{"en":"Well-balanced between tart citrus and sweet nutty elements, with gin providing unusual complexity"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["tiki_bar","summer_gathering","unique_experience"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Southeast Asian cuisine, ceviche, grilled fish, or light seafood dishes. Also pairs well with citrus-marinated chicken and tropical fruit salads."},"ideal_for":{"en":"Perfect for gin lovers curious about tiki culture and adventurous drinkers seeking something different. Ideal for those who find traditional rum-based tiki drinks too heavy or sweet. Great for warm weather refreshment with complexity."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_SYRUP","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Passion fruit syrup","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_FALERNUM","quantity":{"amount":15,"unit":"ml"},"display_name":"Falernum","notes":null},{"ingredient_id":"ING_ORGEAT","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Orgeat syrup","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe or tiki glass. Garnish with lemon wheel and edible flower."}'::jsonb, 'Coupe / Tiki glass', '{"en":"Lemon wheel, edible flower (orchid)"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","tart","nutty"}', 16, 200, 'intermediate', 90, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 1, '{"vegetarian","gluten-free","dairy-free"}', '{"spring","summer"}', '{"tiki_bar","summer_gathering","unique_experience"}', false, false, '{"popo-galsini-saturn"}', 'Created by Popo Galsini in 1967, won tiki competition. Unusual gin-based tiki drink - refreshing alternative to rum cocktails. Orgeat contains almonds. Can substitute passion fruit purée for syrup. Serve up in coupe for elegance or tiki glass for theme.', 'mid', 68, 'https://punchdrink.com/recipes/saturn/', 'Created by J. "Popo" Galsini in 1967. Resurrected by modern tiki revival. Smuggler''s Cove and tiki cocktail archives.', 1)
