-- 203. Scorpion Bowl
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('6f7a8b9c-0d1e-2f3a-4b5c-6d7e8f9a0b1c', 'scorpion-bowl', 'scorpion_bowl_famous_tiki_tropical_cocktail', '{"en":"Scorpion Bowl"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","communal","strong"}', '{"en":"The Scorpion Bowl is a legendary communal tiki drink created by Trader Vic, combining rum, brandy, gin, and fruit juices in a large decorative bowl meant for sharing. Often served flaming with straws for multiple drinkers, this potent punch embodies the social spirit of tiki culture."}'::jsonb, '{"created_year":"1945","origin":{"city":"Oakland","bar":"Trader Vic''s","country":"USA"},"creator":{"name":"Victor \"Trader Vic\" Bergeron","profession":"bartender"},"story":{"en":"The Scorpion Bowl was created in 1945 by Victor \"Trader Vic\" Bergeron at his Oakland restaurant. Trader Vic designed this drink to be shared among groups, served in a large communal bowl with long straws - a presentation that became iconic in tiki culture. The drink''s potent mix of multiple spirits was deliberately designed to \"sting like a scorpion\" after consumption. The theatrical presentation often includes floating a flaming lime half filled with overproof rum on top of the drink, creating a spectacular visual effect. The Scorpion Bowl became a staple of tiki restaurants and Polynesian-themed establishments, symbolizing the communal, escapist nature of mid-century tiki culture. It remains popular for group celebrations."},"named_after":{"en":"Named for its powerful \"sting\" from the potent mix of spirits, which supposedly sneaks up on you like a scorpion."}}'::jsonb, '{"profile":["fruity","citrus","strong"],"description":{"en":"Deceptively smooth and fruity with a powerful alcoholic kick. The Scorpion Bowl combines multiple spirits with bright citrus and tropical fruit flavors, creating a dangerously drinkable punch that lives up to its stinging reputation."},"first_impression":{"en":"Sweet tropical and citrus flavors dominate, masking the serious alcohol content beneath"},"finish":{"en":"Long, warming finish with the \"scorpion sting\" of multiple spirits emerging"},"balance":{"en":"Dangerously balanced - fruit flavors mask substantial alcohol, making it easy to overindulge"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","group_gathering","tiki_bar"],"seasons":["summer","year_round"],"food_pairings":{"en":"Perfect for sharing with Polynesian appetizers, crab rangoon, pu pu platters, egg rolls, or teriyaki wings. Also pairs well with Chinese and Thai cuisine served family-style."},"ideal_for":{"en":"Perfect for group celebrations and party atmospheres. Ideal for 2-4 people sharing a communal drinking experience. Great for birthdays, bachelor/bachelorette parties, or any festive occasion. Warning: deceptively strong - pace yourselves."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":90,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_BRANDY","quantity":{"amount":45,"unit":"ml"},"display_name":"Brandy","notes":null},{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_ORGEAT","quantity":{"amount":30,"unit":"ml"},"display_name":"Orgeat syrup","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'build', '{"en":"Add all ingredients to a large scorpion bowl filled with crushed ice. Stir well to combine and chill. Add more crushed ice to fill. Optionally, float a halved lime shell filled with overproof rum on top and ignite carefully. Serve with long straws for sharing."}'::jsonb, 'Scorpion bowl (communal)', '{"en":"Orchid flowers, pineapple wedges, maraschino cherries, flaming lime half (optional)"}'::jsonb, 'crushed', 'communal', '{"ING_RUM_WHITE","ING_BRANDY","ING_GIN"}', '{"fruity","citrus","strong"}', 15, 1200, 'intermediate', 180, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"summer","year_round"}', '{"party","celebration","group_gathering","tiki_bar"}', false, false, '{"trader-vics-scorpion","scorpion-single-serve"}', 'Created by Trader Vic in 1945. Serves 2-4 people. Orgeat contains almonds. Use scorpion bowl if available, otherwise large tiki bowl. If flaming, use extreme caution - extinguish before drinking. Provide long straws. Very potent - warn guests and monitor consumption.', 'premium', 82, 'https://tradervics.com/cocktails/', 'Original Trader Vic recipe from 1945. Trader Vic''s Bartender''s Guide. Tiki cocktail archives.', 1)
