-- 204. Scotch and Soda
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e1f2a3b4-5678-9012-e567-901234567890', 'scotch-and-soda', 'c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7', '{"en":"Scotch and Soda"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","scotch","whisky","classic","simple"}', '{"en":"The ultimate minimalist cocktail - Scotch whisky lengthened with club soda. This classic highball is beloved for its simplicity, allowing the Scotch''s character to shine while adding refreshing effervescence."}'::jsonb, '{"created_year":"1890","origin":{"city":"United Kingdom","country":"UK"},"story":{"en":"The Scotch and Soda is one of the oldest and simplest highball cocktails, dating back to the late 19th century when carbonated water became commercially available. It gained popularity in gentlemen''s clubs in Britain and later became a staple in American bars during the mid-20th century. The drink became synonymous with sophistication and was the preferred drink of many business executives and Mad Men-era advertising executives. Its enduring appeal lies in its simplicity and the way it showcases quality Scotch without masking its character."},"named_after":{"en":"Simply named after its two ingredients: Scotch whisky and soda water."}}'::jsonb, '{"profile":["smoky","malty","refreshing","clean"],"description":{"en":"The Scotch''s character takes center stage - whether smoky, peaty, or smooth and sweet - diluted just enough to be refreshing without losing complexity. The soda adds effervescence and opens up the whisky''s aromatics, creating a lighter, more sessionable drink."},"first_impression":{"en":"Scotch character (smoky or sweet) with clean effervescence"},"finish":{"en":"Clean finish showcasing the Scotch''s natural character"},"balance":{"en":"Perfect dilution - refreshing without over-diluting the whisky''s complexity"}}'::jsonb, '{"best_time":["evening","afternoon","late_night"],"occasions":["casual","business","social","relaxation"],"seasons":["all-year"],"food_pairings":{"en":"Pairs excellently with Scottish fare, smoked salmon, oysters, aged cheeses, and charcuterie. Perfect with steak, grilled meats, and hearty pub food."},"ideal_for":{"en":"Perfect for Scotch lovers who want a lighter, more refreshing drink. Great for business settings, casual conversations, or anyone who appreciates quality whisky in a more sessionable format. Ideal for warm weather Scotch drinking."}}'::jsonb, '[{"ingredient_id":"ING_SCOTCH_WHISKY","quantity":{"amount":60,"unit":"ml"},"display_name":"Scotch whisky","notes":null},{"ingredient_id":"ING_CLUB_SODA","quantity":{"amount":90,"unit":"ml"},"display_name":"Club soda","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour Scotch over ice. Top with club soda. Stir gently once. Garnish with a lemon twist or serve plain."}'::jsonb, 'Highball glass', '{"en":"Lemon twist (optional)"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_SCOTCH_WHISKY"}', '{"smoky","malty","refreshing","clean"}', 12, 135, 'easy', 20, '{"sulphites","gluten"}', '{"alcohol","sulphites_intolerance","gluten_intolerance"}', '{"vegan","vegetarian","pescatarian","dairy_free","nut_free"}', 0, '{"vegan","dairy-free"}', '{"all-year"}', '{"casual","business","social"}', false, false, '{"bourbon-and-soda","whisky-highball"}', 'Use quality Scotch as it''s the star. Ratio is flexible - some prefer 2:1 soda to Scotch, others prefer equal parts. Stir gently to preserve carbonation. Some purists serve without garnish.', 'mid', 75, 'https://www.diffordsguide.com/cocktails/recipe/1244/scotch-and-soda', 'Classic highball from late 19th century, popularized in gentlemen''s clubs.', 1)
