INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f2a3b4c5-6789-0123-f678-012345678901', 'seven-and-seven', 'd9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8', '{"en":"Seven and Seven"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","whiskey","seagrams","simple","americana"}', '{"en":"An iconic American highball combining Seagram''s 7 Crown whiskey with 7-Up lemon-lime soda. The Seven and Seven is a super-simple, sweet drink that became a cultural phenomenon in American bars and remains a nostalgic favorite."}'::jsonb, '{"created_year":"1970","origin":{"city":"United States","country":"USA"},"story":{"en":"The Seven and Seven emerged in the 1970s as a marketing-driven cocktail that perfectly aligned two popular \"7\" branded products. Seagram''s 7 Crown whiskey (launched in 1934) was already popular, and when combined with 7-Up (launched in 1929), the catchy name and simple recipe made it an instant hit. The drink became ubiquitous in American bars throughout the 1970s and 1980s, appealing to those who wanted an easy-to-order, easy-to-make drink. It represents a distinctly American approach to cocktails - simple, straightforward, and brand-focused."},"named_after":{"en":"Named after its two branded ingredients: Seagram''s 7 Crown whiskey and 7-Up soda."}}'::jsonb, '{"profile":["sweet","citrus","smooth","light"],"description":{"en":"Very sweet and approachable with citrusy lemon-lime notes dominating the smooth, light whiskey base. The 7-Up provides effervescence and sweetness that almost completely masks the alcohol, making it dangerously easy to drink."},"first_impression":{"en":"Sweet lemon-lime soda with subtle whiskey warmth"},"finish":{"en":"Clean, sweet finish with minimal whiskey burn"},"balance":{"en":"Heavily sweet-leaning with the 7-Up dominating the flavor profile"}}'::jsonb, '{"best_time":["evening","afternoon","casual"],"occasions":["casual","party","sports_bar","dive_bar"],"seasons":["all-year"],"food_pairings":{"en":"Pairs well with American comfort food, burgers, pizza, wings, and bar snacks. Works with barbecue and casual pub fare."},"ideal_for":{"en":"Perfect for those who want an easy-drinking, no-fuss cocktail. Great for casual settings, dive bars, and anyone nostalgic for 1970s-80s American bar culture. Ideal for whiskey beginners."}}'::jsonb, '[{"ingredient_id":"ING_SEAGRAMS_7","quantity":{"amount":45,"unit":"ml"},"display_name":"Seagram''s 7 Crown whiskey","notes":null},{"ingredient_id":"ING_SEVEN_UP","quantity":{"amount":120,"unit":"ml"},"display_name":"7-Up or Sprite","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour Seagram''s 7 Crown over ice. Top with 7-Up. Stir gently. Garnish with a lemon wedge if desired."}'::jsonb, 'Highball glass', '{"en":"Lemon wedge (optional)"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_SEAGRAMS_7"}', '{"sweet","citrus","smooth","light"}', 9, 175, 'easy', 20, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"casual","party","sports_bar"}', false, false, '{"whiskey-seven","canadian-seven"}', 'Traditionally made with Seagram''s 7 Crown and 7-Up specifically, though Sprite can substitute. Can be made with other whiskeys but loses the authentic "7 and 7" name. Very popular in 1970s-80s America.', 'low', 60, 'https://www.diffordsguide.com/cocktails/recipe/1245/seven-and-seven', 'Marketing-driven cocktail from 1970s combining two "7" branded products.', 1)
