-- 208. Shark Bite
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9f8e7d6c-5b4a-3e2d-1c0f-9e8d7c6b5a4f', 'shark-bite', 'shark-bite-tiki-tropical-famous-2025', '{"en":"Shark Bite"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","blue-curacao","layered","instagram"}', '{"en":"A visually dramatic cocktail combining rum and blue curaçao with tropical juices, finished with a grenadine \"blood\" drip that creates the appearance of a shark bite. This Instagram-worthy drink is as fun to look at as it is to drink."}'::jsonb, '{"created_year":"1980s","origin":{"city":"Various","bar":"Beach Bars","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Shark Bite emerged in the 1980s during the height of theatrical tiki cocktail culture. Created in beach bars catering to tourists, the drink''s dramatic presentation - featuring a \"bloody\" grenadine drip that resembles a shark bite - made it an instant hit. The cocktail gained popularity at beach resorts and tiki bars, where bartenders would often add theatrical flair by drizzling the grenadine dramatically while telling stories of shark encounters. Its visual appeal has made it a social media favorite in recent years."},"named_after":{"en":"Named for the dramatic visual effect created by the grenadine drip, which resembles blood from a shark bite in the blue ocean-colored drink."}}'::jsonb, '{"profile":["fruity","tropical","sweet"],"description":{"en":"Sweet and tropical with blue curaçao orange notes, rum warmth, and tropical fruit flavors. The grenadine adds a sweet cherry finish. Light, refreshing, and dangerously easy to drink despite its dramatic appearance."},"first_impression":{"en":"Sweet tropical fruit and orange citrus with vibrant blue color"},"finish":{"en":"Sweet cherry-pomegranate finish from grenadine with lingering rum warmth"},"balance":{"en":"Sweet and fruity with good tropical balance - the grenadine adds visual drama without overpowering"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["pool_party","beach","celebration","instagram","themed_party"],"seasons":["summer","year_round"],"food_pairings":{"en":"Perfect with fish and chips, coconut shrimp, ceviche, poke bowls, or tropical fruit platters. Also pairs well with Hawaiian pizza and Caribbean seafood dishes."},"ideal_for":{"en":"Perfect for pool parties, beach bars, and tropical-themed events. Ideal for those who love visually striking cocktails and social media moments. Great for shark week celebrations or ocean-themed parties."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":45,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_BLUE_CURACAO","quantity":{"amount":30,"unit":"ml"},"display_name":"Blue curaçao","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_SWEET_AND_SOUR_MIX","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet and sour mix","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Grenadine","notes":null}]'::jsonb, 'shake', '{"en":"Add rum, blue curaçao, pineapple juice, and sweet and sour mix to a shaker with ice. Shake well and strain into a hurricane glass filled with ice. Slowly drizzle grenadine down the side of the glass to create a \"blood\" effect at the bottom."}'::jsonb, 'Hurricane glass', '{"en":"Orange slice and maraschino cherry, optional gummy shark"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_WHITE"}', '{"fruity","tropical","sweet"}', 12, 250, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","year_round"}', '{"pool_party","beach","celebration","instagram"}', false, false, '{"virgin-shark-bite","frozen-shark-bite","shark-attack"}', 'Drizzle grenadine slowly down the inside of glass for "blood" effect - don''t stir after adding. Very Instagram-worthy - encourage photos before drinking. Can add gummy shark as fun garnish. Sweet and sour mix can be replaced with fresh lemon/lime juice and simple syrup.', 'mid', 75, 'https://www.liquor.com/recipes/shark-bite/', 'Popular beach bar cocktail. Multiple recipe variations exist.', 1)
