-- 209. Siesta
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e5f6a7b8-c9d0-4e1f-2a3b-4c5d6e7f8a9b', 'siesta', 'b2c1d0e9f8a7b6c5948392817069584fbecdac05', '{"en":"Siesta"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","tequila","refreshing","citrus"}', '{"en":"A tequila-based riff on the Hemingway Daiquiri, combining tequila, Campari, grapefruit juice, lime juice, and simple syrup. The Siesta is a refreshing, bitter-sweet cocktail perfect for warm afternoons."}'::jsonb, '{"created_year":"2006","origin":{"city":"New York City","bar":"Flatiron Lounge","country":"USA"},"creator":{"name":"Katie Stipe","profession":"bartender"},"story":{"en":"Created by Katie Stipe at the Flatiron Lounge in New York City in 2006, the Siesta is a tequila-based variation of the classic Hemingway Daiquiri. Stipe substituted tequila for rum and added Campari for bitter complexity, creating a sophisticated cocktail that captures the essence of a relaxing afternoon break. The drink quickly became popular and is now considered a modern classic."},"named_after":{"en":"Named \"Siesta\" to evoke the Spanish/Mexican tradition of afternoon rest, reflecting the drink''s refreshing, relaxing character."}}'::jsonb, '{"profile":["citrus","bitter","refreshing"],"description":{"en":"Bright and refreshing with complex bitter-sweet balance. Tequila provides an agave backbone, grapefruit adds tart citrus notes, Campari contributes sophisticated bitterness, and lime and simple syrup round out the flavor profile."},"first_impression":{"en":"Tart grapefruit and tequila hit first, followed by Campari bitterness and citrus complexity"},"finish":{"en":"Clean, refreshing finish with lingering bitter citrus notes"},"balance":{"en":"Perfectly balanced between tart, bitter, and sweet - refreshing but sophisticated"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["brunch","poolside","summer_party","aperitivo"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with ceviche, grilled seafood, Mexican cuisine, citrus salads, and light appetizers. The bitter-citrus profile complements fresh, bright flavors."},"ideal_for":{"en":"Perfect for those who enjoy refreshing, citrus-forward cocktails with a sophisticated bitter edge. Ideal for tequila and Campari lovers."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_BLANCO","quantity":{"amount":45,"unit":"ml"},"display_name":"Tequila blanco","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":15,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe glass. Garnish with a grapefruit twist."}'::jsonb, 'Coupe', '{"en":"Grapefruit twist"}'::jsonb, 'none', 'up', '{"ING_TEQUILA_BLANCO"}', '{"citrus","bitter","refreshing"}', 16, 165, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","poolside","summer_party","aperitivo"}', false, false, '{"hemingway-daiquiri","paloma"}', 'Use fresh grapefruit juice for best results. The Campari adds complexity but can be reduced for guests who prefer less bitterness. Express grapefruit oils over drink before garnishing.', 'mid', 75, 'https://flatironlounge.com', 'Created by Katie Stipe at Flatiron Lounge, 2006. Modern classic cocktail.', 1)
