INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f6a7b8c9-d0e1-4f2a-3b4c-5d6e7f8a9b0c', 'spring-forward', 'a3b2c1d0e9f8a7b6c594839281706958fecdab06', '{"en":"Spring Forward"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","gin","floral","refreshing"}', '{"en":"A floral and refreshing gin cocktail featuring elderflower liqueur, lemon juice, and Aperol. The Spring Forward captures the essence of springtime in a glass with its bright, botanical flavors."}'::jsonb, '{"created_year":"2009","origin":{"city":"New York City","bar":"PDT (Please Don''t Tell)","country":"USA"},"creator":{"name":"Jim Meehan","profession":"bartender"},"story":{"en":"Created by Jim Meehan at the legendary PDT in New York City in 2009, the Spring Forward was designed as a seasonal cocktail to celebrate the arrival of spring. Meehan combined gin with elderflower liqueur and Aperol to create a drink that perfectly captures the fresh, floral character of the season. The name cleverly references both the spring season and the daylight saving time change."},"named_after":{"en":"Named \"Spring Forward\" referencing both the spring season and the \"spring forward\" daylight saving time change that occurs in spring."}}'::jsonb, '{"profile":["floral","citrus","refreshing"],"description":{"en":"Light, floral, and refreshing with botanical complexity. Gin provides a juniper backbone, elderflower liqueur adds delicate floral sweetness, lemon brings bright acidity, and Aperol contributes subtle bitter-orange notes."},"first_impression":{"en":"Bright elderflower and citrus hit first, followed by gin botanicals and subtle bitterness"},"finish":{"en":"Clean, refreshing finish with lingering floral and citrus notes"},"balance":{"en":"Perfectly balanced between floral sweetness and citrus tartness, light and refreshing"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["brunch","garden_party","spring_celebration","aperitivo"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with spring salads, fresh cheeses, light seafood, asparagus dishes, and herb-forward cuisine. The floral character complements delicate, fresh flavors."},"ideal_for":{"en":"Perfect for gin lovers who enjoy floral, botanical cocktails. Ideal for those seeking a light, refreshing drink with spring-like character."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_ELDERFLOWER_LIQUEUR","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Elderflower liqueur","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":15,"unit":"ml"},"display_name":"Aperol","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe glass. Garnish with an edible flower or lemon twist."}'::jsonb, 'Coupe', '{"en":"Edible flower or lemon twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"floral","citrus","refreshing"}', 18, 155, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","garden_party","spring_celebration","aperitivo"}', false, false, '{"french-75","southside"}', 'Use quality elderflower liqueur (St-Germain recommended). Fresh lemon juice is essential. Edible flowers make a beautiful seasonal garnish for special occasions.', 'mid', 73, 'PDT Cocktail Book by Jim Meehan', 'Created by Jim Meehan at PDT, 2009. Modern classic cocktail.', 1)
