-- 212. Tequila Slammer
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c9d0e1f2-3456-7890-c345-789012345678', 'tequila-slammer', 'a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5', '{"en":"Tequila Slammer"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","tequila","shot","party","interactive","fizzy"}', '{"en":"A festive party drink combining tequila with lemon-lime soda or champagne, served with a slam-and-shoot ritual. The Tequila Slammer is as much about the performance as the taste, creating an effervescent, fizzy shot that''s become a nightclub staple."}'::jsonb, '{"created_year":"1970","origin":{"city":"Mexico","country":"Mexico"},"story":{"en":"The Tequila Slammer (also known as \"Tequila Boom Boom\") originated in Mexico in the 1970s and gained international popularity in the 1980s. The ritual involves covering the glass with your hand, slamming it on the bar to create fizz, then shooting it quickly while it''s still bubbling. The drink became synonymous with party culture and was popularized by Tom Cruise in the 1988 film \"Cocktail.\" Two versions exist: one with lemon-lime soda (7-Up or Sprite) and a more upscale version with champagne."},"named_after":{"en":"Named after the \"slamming\" action of banging the glass on the bar to activate the carbonation before shooting it."}}'::jsonb, '{"profile":["sweet","citrus","fizzy","agave"],"description":{"en":"Bright citrus sweetness with tequila''s agave notes, enhanced by intense carbonation from the slamming action. The fizz creates a unique tingling sensation and makes the drink surprisingly smooth and easy to shoot despite the tequila base."},"first_impression":{"en":"Intense fizz and citrus with agave warmth"},"finish":{"en":"Quick, clean finish with lingering tequila warmth"},"balance":{"en":"The soda sweetness balances the tequila''s bite, while carbonation makes it dangerously easy to drink"}}'::jsonb, '{"best_time":["night","late_night"],"occasions":["party","celebration","nightclub","birthday"],"seasons":["all-year"],"food_pairings":{"en":"Typically consumed as a party shot without food. If pairing, works with Mexican appetizers, nachos, tacos, and salty snacks."},"ideal_for":{"en":"Perfect for party atmospheres and celebrations. Great for groups who enjoy interactive drinking rituals. Popular in nightclubs and birthday parties. Not for those seeking sophisticated cocktails."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_SILVER","quantity":{"amount":30,"unit":"ml"},"display_name":"Tequila (silver)","notes":null},{"ingredient_id":"ING_LEMON_LIME_SODA","quantity":{"amount":30,"unit":"ml"},"display_name":"Lemon-lime soda (7-Up or Sprite)","notes":null}]'::jsonb, 'build', '{"en":"Pour tequila into a rocks glass or highball glass. Add lemon-lime soda. Cover the top of the glass firmly with your hand. Slam the glass down on the bar to create fizz. Immediately shoot the drink while it''s still fizzing."}'::jsonb, 'Rocks glass or Highball glass', '{"en":"None (optional lime wedge)"}'::jsonb, 'none', 'straight_up', '{"ING_TEQUILA_SILVER"}', '{"sweet","citrus","fizzy","agave"}', 20, 95, 'easy', 20, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"party","celebration","nightclub"}', false, false, '{"tequila-boom-boom","champagne-slammer"}', 'Champagne version uses equal parts tequila and champagne - more expensive but smoother. Demonstrate the slam technique to customers. Use sturdy glassware. Popular as a group shot. Equal parts (50/50) ratio is standard.', 'low', 75, 'https://www.diffordsguide.com/cocktails/recipe/1242/tequila-slammer', 'Mexican party shot popularized in 1980s, featured in "Cocktail" (1988).', 1)
