-- 213. Test Pilot
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('7a8b9c0d-1e2f-3a4b-5c6d-7e8f9a0b1c2d', 'test-pilot', 'test_pilot_famous_tiki_tropical_cocktail', '{"en":"Test Pilot"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","complex","strong"}', '{"en":"The Test Pilot is a sophisticated tiki cocktail created by Don the Beachcomber, featuring a complex blend of rums with falernum, Pernod, and citrus. This potent drink showcases the depth and complexity that defines classic tiki mixology."}'::jsonb, '{"created_year":"1941","origin":{"city":"Hollywood","bar":"Don the Beachcomber","country":"USA"},"creator":{"name":"Ernest Raymond Beaumont Gantt (Donn Beach)","profession":"bartender"},"story":{"en":"The Test Pilot was created around 1941 by Don the Beachcomber (Ernest Raymond Beaumont Gantt) at his Hollywood restaurant. The drink emerged during the early days of World War II when aviation was prominent in public consciousness. Don the Beachcomber was known for creating incredibly complex rum cocktails with multiple rums and exotic ingredients, and the Test Pilot exemplifies this approach. The recipe combines three different rums with falernum (a Caribbean spiced syrup), Pernod (anise liqueur), and fresh citrus, creating layers of flavor that were revolutionary for the era. Like many Don the Beachcomber creations, the recipe was kept secret and coded, only being deciphered decades later by tiki historians like Jeff \"Beachbum\" Berry."},"named_after":{"en":"Named after World War II aviation test pilots, reflecting the drink''s daring, adventurous nature and potent strength."}}'::jsonb, '{"profile":["complex","spiced","citrus"],"description":{"en":"Extraordinarily complex with layers of rum, spice, anise, and citrus. The Test Pilot balances multiple rums with the warming spice of falernum, subtle anise from Pernod, and bright lime. Each sip reveals new dimensions of flavor - a true test of bartending mastery."},"first_impression":{"en":"Bright lime and anise hit first, followed by warming spice and complex rum depth"},"finish":{"en":"Long, complex finish with lingering spice, anise, and aged rum notes"},"balance":{"en":"Masterfully balanced complexity - each ingredient plays its part without overwhelming the others"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["tiki_bar","special_occasion","connoisseur_gathering"],"seasons":["year_round"],"food_pairings":{"en":"Perfect with Polynesian-style appetizers, teriyaki dishes, grilled pineapple, or spiced pork. Also pairs well with Caribbean jerk chicken and tropical fruit desserts."},"ideal_for":{"en":"Perfect for serious tiki enthusiasts and rum connoisseurs seeking an authentic Don the Beachcomber experience. Ideal for those who appreciate complex, multi-layered cocktails. Not for beginners - this is advanced tiki drinking."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum (Jamaican)","notes":null},{"ingredient_id":"ING_RUM_DEMERARA","quantity":{"amount":15,"unit":"ml"},"display_name":"Demerara rum (151 proof)","notes":null},{"ingredient_id":"ING_ORANGE_CURACAO","quantity":{"amount":15,"unit":"ml"},"display_name":"Orange curaçao","notes":null},{"ingredient_id":"ING_FALERNUM","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Falernum","notes":null},{"ingredient_id":"ING_PERNOD","quantity":{"amount":3,"unit":"ml"},"display_name":"Pernod (absinthe)","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a double old-fashioned glass filled with crushed ice. Garnish with mint sprig and lime wheel."}'::jsonb, 'Double old-fashioned', '{"en":"Fresh mint sprig, lime wheel"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK","ING_RUM_DEMERARA"}', '{"complex","spiced","citrus"}', 22, 220, 'hard', 120, '{"sulphites","wormwood"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"year_round"}', '{"tiki_bar","special_occasion","connoisseur_gathering"}', false, false, '{"don-the-beachcomber-test-pilot"}', 'Original Don the Beachcomber recipe circa 1941, decoded by Jeff Berry. Complex recipe requires precise measurements. Falernum is essential - contains clove, almond, lime. Just a dash of Pernod - too much will overpower. Use 151-proof Demerara if available. Very strong and complex.', 'premium', 62, 'https://beachbumberry.com/test-pilot.html', 'Original Don the Beachcomber recipe circa 1941, decoded by Jeff "Beachbum" Berry in Sippin'' Safari.', 1)
