-- 216. Tokyo Tea
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f8a9b0c1-2345-6789-f234-678901234567', 'tokyo-tea', 'd5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4', '{"en":"Tokyo Tea"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","strong","party","melon","multi-spirit","green"}', '{"en":"A vibrant green variation of the Long Island Iced Tea, featuring Midori melon liqueur instead of cola. This potent cocktail combines five spirits with sweet and sour mix and lemon-lime soda, creating a dangerously drinkable party favorite."}'::jsonb, '{"created_year":"1990","origin":{"city":"United States","country":"USA"},"story":{"en":"The Tokyo Tea emerged in the 1990s as part of the \"geography-themed iced tea\" trend that included Long Island, Long Beach, Miami, and other variations. The name references Tokyo and Japanese culture through the use of Midori, a Japanese melon liqueur. The vibrant green color makes it visually striking and Instagram-worthy. Like its cousins, it contains multiple spirits making it extremely strong despite tasting sweet and fruity. The drink became popular in nightclubs and college bars where the sweet melon flavor masked its substantial alcohol content."},"named_after":{"en":"Named after Tokyo, Japan, referencing Japanese culture through Midori melon liqueur, which is produced in Japan."}}'::jsonb, '{"profile":["sweet","fruity","melon","strong"],"description":{"en":"Sweet and fruity with prominent melon flavor from Midori, masking the considerable alcohol content from five spirits. The lemon-lime soda adds effervescence while the sweet and sour mix provides citrus balance. Dangerously smooth and deceptively strong."},"first_impression":{"en":"Sweet melon with citrus and fruity complexity"},"finish":{"en":"Long, warming finish with melon sweetness and multi-spirit heat"},"balance":{"en":"Very sweet with melon dominating - the substantial alcohol is well-masked, making it dangerously easy to drink"}}'::jsonb, '{"best_time":["evening","night","late_night"],"occasions":["party","celebration","nightclub","birthday"],"seasons":["all-year"],"food_pairings":{"en":"Best enjoyed as a party drink without food. If pairing, works with Asian fusion appetizers, sushi, and light party snacks."},"ideal_for":{"en":"Perfect for party-goers who love sweet, colorful drinks. Great for celebrations and nightclub settings. WARNING: Extremely high alcohol content - pace yourself and drink responsibly."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":15,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":15,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_GIN","quantity":{"amount":15,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_TEQUILA_SILVER","quantity":{"amount":15,"unit":"ml"},"display_name":"Tequila (silver)","notes":null},{"ingredient_id":"ING_MIDORI","quantity":{"amount":15,"unit":"ml"},"display_name":"Midori melon liqueur","notes":null},{"ingredient_id":"ING_SWEET_AND_SOUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet and sour mix","notes":null},{"ingredient_id":"ING_LEMON_LIME_SODA","quantity":{"amount":30,"unit":"ml"},"display_name":"Lemon-lime soda (7-Up or Sprite)","notes":null}]'::jsonb, 'shake', '{"en":"Add vodka, rum, gin, tequila, Midori, and sweet and sour mix to a cocktail shaker with ice. Shake vigorously. Strain into a tall glass filled with fresh ice. Top with lemon-lime soda. Stir gently. Garnish with a cherry and lemon wheel."}'::jsonb, 'Collins glass or Hurricane glass', '{"en":"Maraschino cherry and lemon wheel"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA","ING_WHITE_RUM","ING_GIN","ING_TEQUILA_SILVER","ING_MIDORI"}', '{"sweet","fruity","melon","strong"}', 22, 260, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"party","celebration","nightclub"}', false, false, '{"long-island-iced-tea","long-beach-iced-tea","miami-iced-tea"}', 'EXTREMELY STRONG - 5 spirits total. The green color makes it visually appealing. Midori is essential for authentic Tokyo Tea. Warn customers about high alcohol content.', 'mid', 70, 'https://www.diffordsguide.com/cocktails/recipe/1251/tokyo-tea', 'Part of 1990s "geography iced tea" trend, featuring Midori melon liqueur.', 1)
