-- 220. Tropical Itch
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-f6a7-8b9c-0d1e-2f3a4b5c6d7e', 'tropical-itch', 'tropical-itch-tiki-tropical-famous-2025', '{"en":"Tropical Itch"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","bourbon","exotic","theatrical"}', '{"en":"A unique tiki cocktail combining rum and bourbon with tropical juices and passion fruit. Famously served with a bamboo back scratcher as a swizzle stick, the Tropical Itch offers a complex blend of Caribbean and American spirits in a theatrical presentation."}'::jsonb, '{"created_year":"1950s","origin":{"city":"Honolulu","bar":"Hawaiian Village Hotel","country":"USA"},"creator":{"name":"Harry Yee","profession":"bartender"},"story":{"en":"The Tropical Itch was created in the 1950s by legendary Hawaiian bartender Harry Yee at the Hawaiian Village Hotel in Waikiki, Honolulu. Yee, who is also credited with creating the Blue Hawaii and being the first to garnish drinks with orchids and paper umbrellas, designed this drink to be both delicious and memorable. The signature element is the bamboo back scratcher that serves as the swizzle stick - a playful nod to the drink''s name and a theatrical touch that made it instantly recognizable. The unusual combination of rum and bourbon was innovative for its time, creating a bridge between tiki culture and classic American cocktails."},"named_after":{"en":"Named for the bamboo back scratcher used as a swizzle stick, playfully suggesting the drink is so good it gives you an irresistible \"itch\" to have another."}}'::jsonb, '{"profile":["fruity","tropical","complex"],"description":{"en":"Complex and layered with tropical passion fruit, citrus brightness, rum sweetness, and bourbon depth. The combination of rum and bourbon creates an unusual but harmonious flavor profile that''s both exotic and familiar. Rich, fruity, and surprisingly sophisticated."},"first_impression":{"en":"Bright tropical passion fruit and citrus with underlying bourbon oakiness"},"finish":{"en":"Long, complex finish with tropical fruit, bourbon warmth, and subtle spice"},"balance":{"en":"Expertly balanced between tropical sweetness and bourbon complexity - unique flavor profile unlike typical tiki drinks"}}'::jsonb, '{"best_time":["evening","late_afternoon"],"occasions":["tiki_bar","special_occasion","cocktail_appreciation","hawaiian_theme"],"seasons":["summer","year_round"],"food_pairings":{"en":"Pairs excellently with Hawaiian poke, teriyaki dishes, grilled pineapple, kalua pork, or tropical fruit platters. Also complements American BBQ surprisingly well due to the bourbon component."},"ideal_for":{"en":"Perfect for tiki enthusiasts and bourbon lovers seeking something unique. Ideal for Hawaiian-themed parties or those who appreciate theatrical cocktail presentation. Great for adventurous drinkers who enjoy complex, unexpected flavor combinations."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_BOURBON","quantity":{"amount":30,"unit":"ml"},"display_name":"Bourbon whiskey","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_SYRUP","quantity":{"amount":30,"unit":"ml"},"display_name":"Passion fruit syrup","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a tall tiki glass or hurricane glass filled with crushed ice. Garnish with pineapple wedge, cherry, and the signature bamboo back scratcher swizzle stick."}'::jsonb, 'Tiki glass / Hurricane glass', '{"en":"Pineapple wedge, maraschino cherry, and bamboo back scratcher"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK","ING_BOURBON"}', '{"fruity","tropical","complex"}', 15, 260, 'intermediate', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","year_round"}', '{"tiki_bar","special_occasion","cocktail_appreciation"}', false, false, '{"virgin-tropical-itch"}', 'Created by Harry Yee, inventor of Blue Hawaii. Signature bamboo back scratcher is essential for authentic presentation. Unusual rum-bourbon combo - explain to curious guests. Use fresh juices for best results. Theatrical presentation makes great conversation starter.', 'mid', 65, 'https://www.diffordsguide.com/cocktails/recipe/4686/tropical-itch', 'Classic Harry Yee creation from Hawaiian Village Hotel, Honolulu.', 1)
