-- 221. Twentieth Century
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f8e4a9d3-5b2c-4f1d-9c6e-2b3c4d5e6f7a', 'twentieth-century', 'c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2c3d4', '{"en":"Twentieth Century"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","elegant","white"}', '{"en":"An elegant white cocktail featuring gin, Lillet Blanc, white crème de cacao, and lemon juice. The Twentieth Century is a sophisticated blend of botanical, floral, and subtle chocolate notes with bright citrus balance."}'::jsonb, '{"created_year":"1937","origin":{"city":"London","bar":"Unknown","country":"UK"},"creator":{"name":"C.A. Tuck","profession":"bartender"},"story":{"en":"Created by British bartender C.A. Tuck and published in the \"Café Royal Cocktail Book\" (1937). Named after the luxurious 20th Century Limited train that ran between New York and Chicago from 1902-1967. This cocktail celebrates the glamour and sophistication of early 20th-century luxury travel. The white color evokes the train''s elegant dining cars and Art Deco aesthetic."},"named_after":{"en":"Named after the legendary 20th Century Limited luxury train that symbolized elegance, speed, and sophistication in early 20th-century America."}}'::jsonb, '{"profile":["elegant","floral","citrus"],"description":{"en":"Sophisticated and elegant with gin botanicals, Lillet''s floral-wine notes, subtle white chocolate from crème de cacao, and bright lemon acidity. Delicate yet complex with a beautiful white appearance."},"first_impression":{"en":"Bright lemon and floral Lillet notes with subtle chocolate hints"},"finish":{"en":"Clean, elegant finish with lingering botanicals and gentle sweetness"},"balance":{"en":"Perfectly balanced elegance - bright yet subtle, complex yet refined"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","aperitivo","date_night","celebration"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light seafood, oysters, delicate desserts, or as an elegant aperitif. Pairs beautifully with French cuisine and white chocolate."},"ideal_for":{"en":"Perfect for gin lovers seeking elegant, white cocktails with subtle complexity. Ideal for those who appreciate delicate flavors and sophisticated presentation."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LILLET_BLANC","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Lillet Blanc","notes":null},{"ingredient_id":"ING_WHITE_CREME_DE_CACAO","quantity":{"amount":15,"unit":"ml"},"display_name":"White crème de cacao","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Double strain into a chilled coupe or cocktail glass. Garnish with a lemon twist, expressing the oils over the drink."}'::jsonb, 'Coupe glass', '{"en":"Lemon twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"elegant","floral","citrus"}', 22, 170, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","aperitivo","date_night","celebration"}', false, false, '{"twentieth-century-moderne"}', 'Use WHITE crème de cacao to maintain the elegant white color. Lillet Blanc essential - do not substitute with vermouth. Double strain for perfect clarity. Fresh lemon juice only. The drink should be pristine white like the train''s dining cars.', 'mid', 60, 'https://www.diffordsguide.com/cocktails/recipe/1556/twentieth-century-cocktail', 'Café Royal Cocktail Book (1937). Named after the legendary 20th Century Limited luxury train.', 1)
