-- 222. Vegas Bomb
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('g7h8i9j0-1234-5678-gh78-ij90kl123456', 'vegas-bomb', 'vegas_bomb_shot_famous_energy_drink_bomb_coconut', '{"en":"Vegas Bomb"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","bomb","energy-drink","party","fruity"}', '{"en":"A fruity bomb shot featuring Crown Royal whisky, peach schnapps, Malibu coconut rum, and cranberry juice, dropped into Red Bull. This party favorite combines tropical flavors with an energy boost, embodying the wild spirit of Las Vegas nightlife."}'::jsonb, '{"created_year":"2000s","origin":{"city":"Las Vegas","bar":"Unknown","country":"USA"},"story":{"en":"The Vegas Bomb emerged in Las Vegas during the early 2000s as a sweeter, more approachable alternative to the Jäger Bomb. Created to appeal to a broader audience who wanted the energy drink bomb experience without the intense herbal flavors, it quickly became a staple in Vegas bars and spread to nightclubs nationwide."},"named_after":{"en":"Named after Las Vegas, the city where it was created and popularized in the casino nightclub scene."}}'::jsonb, '{"profile":["sweet","fruity","tropical","energizing"],"description":{"en":"Sweet and tropical with peach, coconut, and cranberry flavors. The Crown Royal adds smoothness while the Red Bull provides sweetness and energy. Much sweeter and fruitier than a Jäger Bomb."},"first_impression":{"en":"Sweet tropical fruit flavors with energy drink base"},"finish":{"en":"Smooth, fruity finish with energizing caffeine kick"},"balance":{"en":"Very sweet and approachable, designed for easy drinking"}}'::jsonb, '{"best_time":["late_night"],"occasions":["party","nightlife","celebration","vegas_trip","bachelor_bachelorette"],"seasons":["all_seasons"],"food_pairings":{"en":"Not typically paired with food. Best as a standalone party shot or nightclub drink."},"ideal_for":{"en":"Perfect for Vegas trips, bachelorette parties, and nightclub celebrations. Great for those who want a sweeter alternative to Jäger Bombs. WARNING: Contains high amounts of caffeine and alcohol."}}'::jsonb, '[{"ingredient_id":"ING_CANADIAN_WHISKY","quantity":{"amount":15,"unit":"ml"},"display_name":"Crown Royal Whisky","notes":null},{"ingredient_id":"ING_PEACH_SCHNAPPS","quantity":{"amount":15,"unit":"ml"},"display_name":"Peach Schnapps","notes":null},{"ingredient_id":"ING_MALIBU","quantity":{"amount":15,"unit":"ml"},"display_name":"Malibu Coconut Rum","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Cranberry Juice","notes":null},{"ingredient_id":"ING_RED_BULL","quantity":{"amount":120,"unit":"ml"},"display_name":"Red Bull (or energy drink)","notes":null}]'::jsonb, 'build', '{"en":"Fill a pint glass halfway with Red Bull or energy drink. In a shot glass, combine Crown Royal, peach schnapps, Malibu, and cranberry juice. Drop the shot glass into the pint glass and consume immediately."}'::jsonb, 'Shot glass (dropped into pint glass)', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_CANADIAN_WHISKY","ING_PEACH_SCHNAPPS","ING_MALIBU"}', '{"sweet","fruity","tropical","energizing"}', 10, 220, 'easy', 45, '{"sulphites","caffeine"}', '{"alcohol","sulphites_intolerance","caffeine_sensitivity"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"all-seasons"}', '{"party","nightlife","celebration"}', false, false, '{"irish-vegas-bomb","tropical-vegas-bomb","cherry-vegas-bomb"}', 'Mix shot ingredients in shot glass before dropping. Some variations use different ratios or substitute ingredients. WARNING: High caffeine and alcohol content - serve responsibly. Very popular for Vegas trips and bachelor/bachelorette parties.', 'mid', 85, 'https://www.liquor.com/recipes/vegas-bomb/', 'Popular Las Vegas bomb shot from the early 2000s.', 1)
