INSERT INTO southafrican (id, slug, name, description, category, status, region, protein_type, cooking_method, prep_time_min, spice_level, dietary, allergens, tags, popularity) VALUES
('ZA_BOEREWORS', 'boerewors', 'Boerewors', 'Traditional South African farmer''s sausage made with beef, pork, and spices including coriander. Coiled and grilled over open flames at every braai.', 'braai', 'iconic', 'National', 'beef', 'grilled', 30, 2, '{"is_vegetarian": false, "is_vegan": false, "is_gluten_free": true, "is_dairy_free": true}', '{}', '{"sausage", "braai", "grilled", "traditional"}', 95),
