('ZA_BRAAI_LAMB_CHOPS', 'braai-lamb-chops', 'Braai Lamb Chops', 'Succulent lamb chops grilled to perfection over wood coals, a staple at every South African braai gathering.', 'braai', 'iconic', 'National', 'lamb', 'grilled', 25, 1, '{"is_vegetarian": false, "is_vegan": false, "is_gluten_free": true, "is_dairy_free": true}', '{}', '{"lamb", "braai", "grilled", "simple"}', 92),
('ZA_PERI_PERI_CHICKEN', 'peri-peri-chicken', 'Peri-Peri Chicken', 'Portuguese-African grilled chicken marinated in fiery peri-peri (African bird''s eye chili) sauce with garlic and lemon.', 'braai', 'iconic', 'National', 'chicken', 'grilled', 40, 4, '{"is_vegetarian": false, "is_vegan": false, "is_gluten_free": true, "is_dairy_free": true}', '{}', '{"spicy", "portuguese_influence", "grilled", "peri_peri"}', 94),
('ZA_BRAAIBROODJIE', 'braaibroodjie', 'Braaibroodjie', 'Grilled cheese sandwich made on the braai with tomato, onion, and sometimes chutney. The quintessential braai side dish.', 'braai', 'classic', 'National', NULL, 'grilled', 15, 1, '{"is_vegetarian": true, "is_vegan": false, "is_gluten_free": false, "is_dairy_free": false}', '{"gluten", "dairy"}', '{"sandwich", "cheese", "braai", "vegetarian"}', 85);
INSERT INTO southafrican (id, slug, name, description, category, status, region, protein_type, cooking_method, prep_time_min, spice_level, dietary, allergens, tags, popularity) VALUES
