('ZA_BUNNY_CHOW_LAMB', 'bunny-chow-lamb', 'Lamb Bunny Chow', 'Lamb version of the classic bunny chow, with tender slow-cooked lamb curry in a bread bowl.', 'curry', 'classic', 'KwaZulu-Natal', 'lamb', 'stewed', 120, 4, '{"is_vegetarian": false, "is_vegan": false, "is_gluten_free": false, "is_dairy_free": true}', '{"gluten"}', '{"street_food", "durban", "lamb", "spicy"}', 85),
('ZA_CAPE_MALAY_CURRY', 'cape-malay-curry', 'Cape Malay Curry', 'Aromatic curry with fruity notes from the Cape Malay community. Features apricots, raisins, and warming spices.', 'curry', 'classic', 'Western Cape', 'chicken', 'stewed', 75, 2, '{"is_vegetarian": false, "is_vegan": false, "is_gluten_free": true, "is_dairy_free": true}', '{}', '{"cape_malay", "fruity", "aromatic", "curry"}', 82),
